import os
import logging
from typing import Dict, List, Optional
from .providers.config import ProviderConfigManager
from .providers.models import (
    BaseProvider,
    ChatCompletionRequest,
    ChatCompletionResponse,
    ModelConfig,
    ProviderConfig,
)
from .providers.openai_provider import OpenAIProvider

# from .providers.ollama_provider import OllamaProvider  # Uncomment if implemented

logger = logging.getLogger(__name__)


class ProviderManager:
    """Manager for AI providers."""

    def __init__(self):
        self.config_manager = ProviderConfigManager()
        self.providers: Dict[str, BaseProvider] = {}
        self._initialize_providers()

    def _initialize_providers(self):
        """Initialize available providers."""
        try:
            # Initialize OpenAI provider if API key is available
            openai_config = ProviderConfig(
                name="openai",
                type="openai",
                api_key=self._get_env_var("OPENAI_API_KEY"),
                enabled=True,
            )
            if openai_config.api_key:
                self.providers["openai"] = OpenAIProvider(openai_config)
                logger.info("OpenAI provider initialized")
            # Initialize Ollama provider (if implemented)
            # ollama_config = ProviderConfig(name="ollama", type="ollama", enabled=True)
            # self.providers["ollama"] = OllamaProvider(ollama_config)
            # logger.info("Ollama provider initialized")
        except Exception as e:
            logger.error(f"Failed to initialize providers: {e}")

    def _get_env_var(self, name: str) -> Optional[str]:
        """Get environment variable safely."""
        try:
            return os.getenv(name)
        except Exception:
            return None

    def get_provider(self, name: str) -> Optional[BaseProvider]:
        """Get a provider by name."""
        return self.providers.get(name)

    def list_providers(self) -> List[str]:
        """List all available providers."""
        return list(self.providers.keys())

    async def get_available_models(self) -> List[ModelConfig]:
        """Get all available models from all providers."""
        models = []
        for provider_name, provider in self.providers.items():
            try:
                if await provider.is_available():
                    provider_models = await provider.list_models()
                    models.extend(provider_models)
            except Exception as e:
                logger.error(f"Failed to get models from {provider_name}: {e}")
        return models

    async def chat_completion(
        self, provider_name: str, request: ChatCompletionRequest
    ) -> ChatCompletionResponse:
        """Perform chat completion with specified provider."""
        provider = self.get_provider(provider_name)
        if not provider:
            raise ValueError(f"Provider {provider_name} not found")
        if not await provider.is_available():
            raise RuntimeError(f"Provider {provider_name} is not available")
        return await provider.chat_completion(request)

    def get_pricing(self, provider: str, model: str) -> Dict[str, float]:
        """Get pricing for a provider and model."""
        return self.config_manager.get_pricing(provider, model)

    def update_pricing(
        self, provider: str, model: str, input_cost: float, output_cost: float
    ) -> None:
        """Update pricing for a provider and model."""
        self.config_manager.update_pricing(provider, model, input_cost, output_cost)


# Global provider manager instance
provider_manager = ProviderManager()


def get_provider_manager() -> ProviderManager:
    """Get the global provider manager instance."""
    return provider_manager

"""
OpenAI API provider implementation.
"""

import logging
import os
from typing import Dict, List, Optional

try:
    import openai

    OPENAI_AVAILABLE = True
except ImportError:
    OPENAI_AVAILABLE = False

from .models import (
    BaseProvider,
    ChatCompletionRequest,
    ChatCompletionResponse,
    ModelConfig,
    ProviderConfig,
)

logger = logging.getLogger(__name__)


class OpenAIProvider(BaseProvider):
    """OpenAI API provider."""

    def _setup_client(self):
        """Setup OpenAI client."""
        if not OPENAI_AVAILABLE:
            raise ImportError("OpenAI library not available")

        try:
            api_key = self.config.api_key or os.getenv("OPENAI_API_KEY")
            if not api_key:
                raise ValueError("OpenAI API key not found")

            openai.api_key = api_key
            if self.config.base_url:
                openai.base_url = self.config.base_url

            self.client = openai
            logger.info("OpenAI client initialized successfully")

        except Exception as e:
            logger.error(f"Failed to setup OpenAI client: {e}")
            raise

    async def is_available(self) -> bool:
        """Check if OpenAI is available."""
        try:
            # Simple availability check
            return bool(self.client and self.config.api_key)
        except Exception as e:
            logger.error(f"OpenAI availability check failed: {e}")
            return False

    async def chat_completion(
        self, request: ChatCompletionRequest
    ) -> ChatCompletionResponse:
        """Perform chat completion with OpenAI."""
        try:
            # Prepare messages
            messages = []
            if request.system_prompt:
                messages.append({"role": "system", "content": request.system_prompt})

            messages.extend(request.messages)

            # Prepare parameters
            params = {
                "model": request.model,
                "messages": messages,
                "temperature": request.temperature,
                "top_p": request.top_p,
                "frequency_penalty": request.frequency_penalty,
                "presence_penalty": request.presence_penalty,
            }

            if request.max_tokens:
                params["max_tokens"] = request.max_tokens

            if request.stop:
                params["stop"] = request.stop

            if request.stream:
                params["stream"] = True

            # Make API call
            response = await self.client.chat.completions.create(**params)

            # Extract content
            content = response.choices[0].message.content
            usage = {
                "prompt_tokens": response.usage.prompt_tokens,
                "completion_tokens": response.usage.completion_tokens,
                "total_tokens": response.usage.total_tokens,
            }

            # Calculate cost
            cost = self.calculate_cost(usage, request.model)

            return ChatCompletionResponse(
                model=request.model,
                content=content,
                usage=usage,
                finish_reason=response.choices[0].finish_reason,
                cost=cost,
            )

        except Exception as e:
            logger.error(f"OpenAI chat completion failed: {e}")
            raise

    async def list_models(self) -> List[ModelConfig]:
        """List available OpenAI models."""
        try:
            models = []

            # Common OpenAI models
            openai_models = [
                {
                    "name": "gpt-4",
                    "context_length": 8192,
                    "max_tokens": 4096,
                    "capabilities": ["chat", "code-generation"],
                },
                {
                    "name": "gpt-4-turbo",
                    "context_length": 128000,
                    "max_tokens": 4096,
                    "capabilities": ["chat", "code-generation"],
                },
                {
                    "name": "gpt-3.5-turbo",
                    "context_length": 4096,
                    "max_tokens": 4096,
                    "capabilities": ["chat", "code-generation"],
                },
            ]

            for model_info in openai_models:
                pricing = self._get_cost_per_1k_tokens(model_info["name"])
                models.append(
                    ModelConfig(
                        name=model_info["name"],
                        provider="openai",
                        model_type="chat",
                        context_length=model_info["context_length"],
                        max_tokens=model_info["max_tokens"],
                        capabilities=model_info["capabilities"],
                        pricing=pricing,
                    )
                )

            return models

        except Exception as e:
            logger.error(f"Failed to list OpenAI models: {e}")
            return []

    def _get_context_length(self, model: str) -> Optional[int]:
        """Get context length for a model."""
        context_lengths = {
            "gpt-4": 8192,
            "gpt-4-turbo": 128000,
            "gpt-3.5-turbo": 4096,
        }
        return context_lengths.get(model)

    def _get_cost_per_1k_tokens(self, model: str) -> Optional[float]:
        """Get cost per 1k tokens for a model."""
        costs = {
            "gpt-4": {"input": 0.03, "output": 0.06},
            "gpt-4-turbo": {"input": 0.01, "output": 0.03},
            "gpt-3.5-turbo": {"input": 0.0015, "output": 0.002},
        }
        return costs.get(model, {"input": 0.0, "output": 0.0})

    def _get_capabilities(self, model: str) -> List[str]:
        """Get capabilities for a model."""
        capabilities = {
            "gpt-4": ["chat", "code-generation", "analysis"],
            "gpt-4-turbo": ["chat", "code-generation", "analysis"],
            "gpt-3.5-turbo": ["chat", "code-generation"],
        }
        return capabilities.get(model, ["chat"])

    def calculate_cost(self, usage: Dict[str, int], model: str) -> float:
        """Calculate cost for usage."""
        try:
            pricing = self._get_cost_per_1k_tokens(model)
            if not pricing:
                return 0.0

            input_cost = (usage.get("prompt_tokens", 0) / 1000) * pricing["input"]
            output_cost = (usage.get("completion_tokens", 0) / 1000) * pricing["output"]

            return input_cost + output_cost

        except Exception as e:
            logger.error(f"Failed to calculate cost: {e}")
            return 0.0

