            # self.providers["ollama"] = OllamaProvider(ollama_config)
            # logger.info("Ollama provider initialized")
        except Exception as e:
            logger.error(f"Failed to initialize providers: {e}")

    def _get_env_var(self, name: str) -> Optional[str]:
        """Get environment variable safely."""
        try:
            return os.getenv(name)
        except Exception:
            return None

    def get_provider(self, name: str) -> Optional[BaseProvider]:
        """Get a provider by name."""
        return self.providers.get(name)

    def list_providers(self) -> List[str]:
        """List all available providers."""
        return list(self.providers.keys())

    async def get_available_models(self) -> List[ModelConfig]:
        """Get all available models from all providers."""
        models = []
        for provider_name, provider in self.providers.items():
            try:
                if await provider.is_available():
                    provider_models = await provider.list_models()
                    models.extend(provider_models)
            except Exception as e:
                logger.error(f"Failed to get models from {provider_name}: {e}")
        return models

    async def chat_completion(
        self, provider_name: str, request: ChatCompletionRequest
    ) -> ChatCompletionResponse:
        """Perform chat completion with specified provider."""
        provider = self.get_provider(provider_name)
        if not provider:
            raise ValueError(f"Provider {provider_name} not found")
        if not await provider.is_available():
            raise RuntimeError(f"Provider {provider_name} is not available")
        return await provider.chat_completion(request)

    def get_pricing(self, provider: str, model: str) -> Dict[str, float]:
        """Get pricing for a provider and model."""
        return self.config_manager.get_pricing(provider, model)

    def update_pricing(
        self, provider: str, model: str, input_cost: float, output_cost: float
    ) -> None:
        """Update pricing for a provider and model."""
        self.config_manager.update_pricing(provider, model, input_cost, output_cost)


# Global provider manager instance
provider_manager = ProviderManager()


def get_provider_manager() -> ProviderManager:
    """Get the global provider manager instance."""
    return provider_manager

