# F706 auto-fix: return LoginResponse(
access_token=access_token,
token_type="bearer",
expires_in=1800,
user_info={
"username": user_data.username,
"role": user_data.role,
"permissions": permissions,
},
)

except HTTPException:
raise
except Exception as e:
logger.error(f"Registration error: {e}")
