#!/usr/bin/env python3
"""
Sequential Thinking MCP Server

A Model Context Protocol server that provides sequential thinking capabilities
for local AI models, enabling step-by-step reasoning for complex coding tasks.

This server integrates with the AI Coder Assistant's sequential thinking service
to provide structured problem-solving capabilities through MCP.
"""

import asyncio
import json
import logging
import sys
from pathlib import Path
from typing import Any, Dict, List, Optional, Sequence

# Add parent directory to path for imports
sys.path.append(str(Path(__file__).parent.parent))

from mcp.server.models import InitializationOptions
from mcp.server import NotificationOptions, Server
from mcp.server.stdio import stdio_server
from mcp.types import (
    CallToolResult,
    EmptyResult,
    ListToolsResult,
    TextContent,
    Tool,
    INVALID_PARAMS,
    INTERNAL_ERROR,
)

from src.backend.services.sequential_thinking_service import (
    get_sequential_thinking_engine,
    ReasoningTask,
    TaskComplexity,
    ReasoningStrategy,
    StepStatus
)
from src.backend.services.logging_config import setup_logging

# Configure logging
logger = setup_logging(__name__)

# MCP Server instance
app = Server("sequential-thinking")

# Tool definitions
TOOLS = [
    Tool(
        name="analyze_problem",
        description="Analyze a problem using sequential thinking and step-by-step reasoning",
        inputSchema={
            "type": "object",
            "properties": {
                "description": {
                    "type": "string",
                    "description": "Description of the problem to solve"
                },
                "domain": {
                    "type": "string",
                    "description": "Domain of the problem (code_analysis, architecture, debugging, etc.)",
                    "enum": ["general", "code_analysis", "architecture", "debugging", "refactoring", "optimization", "security", "testing"],
                    "default": "general"
                },
                "complexity": {
                    "type": "string",
                    "description": "Complexity level of the task",
                    "enum": ["low", "medium", "high", "critical"],
                    "default": "medium"
                },
                "strategy": {
                    "type": "string",
                    "description": "Reasoning strategy to use",
                    "enum": ["hierarchical", "iterative", "chain_of_thought", "self_correction", "parallel"],
                    "default": "hierarchical"
                },
                "constraints": {
                    "type": "array",
                    "items": {"type": "string"},
                    "description": "List of constraints for the task",
                    "default": []
                },
                "context": {
                    "type": "object",
                    "description": "Additional context for the task",
                    "default": {}
                },
                "max_steps": {
                    "type": "integer",
                    "description": "Maximum number of reasoning steps",
                    "minimum": 1,
                    "maximum": 50,
                    "default": 10
                }
            },
            "required": ["description"]
        }
    ),
    Tool(
        name="decompose_problem",
        description="Decompose a complex problem into logical steps without executing them",
        inputSchema={
            "type": "object",
            "properties": {
                "description": {
                    "type": "string",
                    "description": "Description of the problem to decompose"
                },
                "domain": {
                    "type": "string",
                    "description": "Domain of the problem",
