@app.call_tool()
async def handle_call_tool(name: str, arguments: Dict[str, Any]) -> CallToolResult:
    """Handle tool calls for sequential thinking operations"""
    try:
        logger.info(f"Handling tool call: {name} with arguments: {arguments}")

        if name == "analyze_problem":
            return await analyze_problem(arguments)
        elif name == "decompose_problem":
            return await decompose_problem(arguments)
        elif name == "execute_step":
            return await execute_step(arguments)
        elif name == "get_model_capabilities":
            return await get_model_capabilities(arguments)
        elif name == "validate_reasoning":
            return await validate_reasoning(arguments)
        elif name == "optimize_reasoning":
            return await optimize_reasoning(arguments)
        else:
            return CallToolResult(
                content=[TextContent(type="text", text=f"Unknown tool: {name}")],
                isError=True
            )

    except Exception as e:
        logger.error(f"Tool call failed: {e}")
        return CallToolResult(
            content=[TextContent(type="text", text=f"Tool execution failed: {str(e)}")],
            isError=True
        )


async def analyze_problem(arguments: Dict[str, Any]) -> CallToolResult:
    """Analyze a problem using sequential thinking"""
    try:
        # Extract parameters
        description = arguments.get("description", "")
        domain = arguments.get("domain", "general")
        complexity = TaskComplexity(arguments.get("complexity", "medium"))
        strategy = ReasoningStrategy(arguments.get("strategy", "hierarchical"))
        constraints = arguments.get("constraints", [])
        context = arguments.get("context", {})
        max_steps = arguments.get("max_steps", 10)

        # Validate required parameters
        if not description:
            return CallToolResult(
                content=[TextContent(type="text", text="Description is required")],
                isError=True
            )

        # Get sequential thinking engine
        engine = await get_sequential_thinking_engine()

        # Create reasoning task
        import uuid
        task = ReasoningTask(
            task_id=str(uuid.uuid4()),
            description=description,
            complexity=complexity,
            domain=domain,
            constraints=constraints,
            expected_output="Complete sequential analysis",
            context=context,
            strategy=strategy,
            max_steps=max_steps
        )

        # Process the task
        result = await engine.process_task(task)

        # Format response
        response = {
            "success": result.success,
            "task_id": result.task_id,
            "steps": [
                {
                    "step_number": step.step_number,
                    "description": step.description,
                    "status": step.status.value,
                    "confidence": step.confidence,
                    "execution_time": step.execution_time,
                    "output": step.output_result or {},
                    "error": step.error_message
                }
                for step in result.steps
            ],
            "final_output": result.final_output,
            "confidence": result.confidence,
            "quality_score": result.quality_score,
            "total_time": result.total_time,
            "recommendations": result.recommendations,
            "strategy_used": result.strategy_used.value,
            "model_used": result.model_used
        }

        return CallToolResult(
            content=[TextContent(type="text", text=json.dumps(response, indent=2))]
        )

    except Exception as e:
        logger.error(f"Problem analysis failed: {e}")
        return CallToolResult(
            content=[TextContent(type="text", text=f"Analysis failed: {str(e)}")],
            isError=True
        )


