async def validate_reasoning(arguments: Dict[str, Any]) -> CallToolResult:
    """Validate the quality and correctness of reasoning results"""
    try:
        # Extract parameters
        reasoning_result = arguments.get("reasoning_result", {})
        validation_criteria = arguments.get("validation_criteria", ["logical_consistency", "completeness", "accuracy"])

        # Validate required parameters
        if not reasoning_result:
            return CallToolResult(
                content=[TextContent(type="text", text="Reasoning result is required")],
                isError=True
            )

        steps = reasoning_result.get("steps", [])
        final_output = reasoning_result.get("final_output", {})
        confidence = reasoning_result.get("confidence", 0.0)

        # Perform validation
        validation_results = {}
        overall_score = 0.0

        # Logical consistency check
        if "logical_consistency" in validation_criteria:
            consistency_score = validate_logical_consistency(steps)
            validation_results["logical_consistency"] = {
                "score": consistency_score,
                "description": "Steps follow logical sequence and dependencies"
            }
            overall_score += consistency_score

        # Completeness check
        if "completeness" in validation_criteria:
            completeness_score = validate_completeness(steps, final_output)
            validation_results["completeness"] = {
                "score": completeness_score,
                "description": "All necessary steps are present and executed"
            }
            overall_score += completeness_score

        # Accuracy check
        if "accuracy" in validation_criteria:
            accuracy_score = validate_accuracy(steps, confidence)
            validation_results["accuracy"] = {
                "score": accuracy_score,
                "description": "Steps produce accurate and reliable results"
            }
            overall_score += accuracy_score

        # Calculate overall score
        overall_score = overall_score / len(validation_criteria)

        # Generate recommendations
        recommendations = []
        if overall_score < 0.7:
            recommendations.append("Consider increasing step granularity")
        if confidence < 0.8:
            recommendations.append("Consider using self-correction strategy")
        if len(steps) < 3:
            recommendations.append("Consider breaking down problem into more steps")

        response = {
            "validation_results": validation_results,
            "overall_score": overall_score,
            "confidence": confidence,
            "recommendations": recommendations,
            "validated_criteria": validation_criteria,
            "validation_passed": overall_score >= 0.7
        }

        return CallToolResult(
            content=[TextContent(type="text", text=json.dumps(response, indent=2))]
        )

    except Exception as e:
        logger.error(f"Reasoning validation failed: {e}")
        return CallToolResult(
            content=[TextContent(type="text", text=f"Validation failed: {str(e)}")],
            isError=True
        )


async def optimize_reasoning(arguments: Dict[str, Any]) -> CallToolResult:
    """Optimize reasoning parameters based on task characteristics"""
    try:
        # Extract parameters
        task_description = arguments.get("task_description", "")
        performance_target = arguments.get("performance_target", "accuracy")
        constraints = arguments.get("constraints", {})

        # Validate required parameters
        if not task_description:
            return CallToolResult(
                content=[TextContent(type="text", text="Task description is required")],
                isError=True
            )

        # Analyze task characteristics
        task_analysis = analyze_task_characteristics(task_description)

        # Generate optimized parameters
        optimized_params = generate_optimized_parameters(
            task_analysis,
            performance_target,
            constraints
        )

        # Generate optimization recommendations
        recommendations = generate_optimization_recommendations(
            task_analysis,
            performance_target,
            constraints
        )

        response = {
            "task_analysis": task_analysis,
            "performance_target": performance_target,
            "optimized_parameters": optimized_params,
            "recommendations": recommendations,
            "constraints_applied": constraints
        }

        return CallToolResult(
            content=[TextContent(type="text", text=json.dumps(response, indent=2))]
        )

    except Exception as e:
        logger.error(f"Reasoning optimization failed: {e}")
        return CallToolResult(
            content=[TextContent(type="text", text=f"Optimization failed: {str(e)}")],
            isError=True
        )


# Helper functions for validation and optimization
def validate_logical_consistency(steps: List[Dict[str, Any]]) -> float:
    """Validate logical consistency of reasoning steps"""
    if not steps:
        return 0.0

    consistency_score = 1.0

    # Check for logical flow
    for i, step in enumerate(steps):
        if step.get("status") == "failed":
            consistency_score -= 0.1

        # Check if step builds on previous steps
        if i > 0 and not step.get("dependencies"):
            consistency_score -= 0.05

    return max(0.0, consistency_score)


def validate_completeness(steps: List[Dict[str, Any]], final_output: Dict[str, Any]) -> float:
    """Validate completeness of reasoning process"""
    if not steps or not final_output:
        return 0.0

    completeness_score = 0.5  # Base score

    # Check if all steps are completed
    completed_steps = [s for s in steps if s.get("status") == "completed"]
    completion_rate = len(completed_steps) / len(steps)
    completeness_score += completion_rate * 0.3

    # Check if final output is meaningful
    if final_output.get("solution") or final_output.get("steps_summary"):
        completeness_score += 0.2

    return min(1.0, completeness_score)


def validate_accuracy(steps: List[Dict[str, Any]], confidence: float) -> float:
    """Validate accuracy of reasoning results"""
    if not steps:
        return 0.0

    # Use confidence as primary accuracy indicator
    accuracy_score = confidence

    # Adjust based on step quality
    high_confidence_steps = [s for s in steps if s.get("confidence", 0) > 0.8]
    if high_confidence_steps:
        accuracy_score += 0.1

    # Penalty for errors
    error_steps = [s for s in steps if s.get("error")]
    if error_steps:
        accuracy_score -= len(error_steps) * 0.1

    return max(0.0, min(1.0, accuracy_score))


def analyze_task_characteristics(task_description: str) -> Dict[str, Any]:
    """Analyze task characteristics for optimization"""
    analysis = {
        "complexity": "medium",
        "domain": "general",
        "keywords": [],
        "estimated_steps": 5,
        "recommended_strategy": "hierarchical"
    }

    description_lower = task_description.lower()

    # Analyze complexity
    if any(word in description_lower for word in ["complex", "comprehensive", "detailed", "multi"]):
        analysis["complexity"] = "high"
        analysis["estimated_steps"] = 10
    elif any(word in description_lower for word in ["simple", "basic", "quick"]):
        analysis["complexity"] = "low"
        analysis["estimated_steps"] = 3

    # Analyze domain
    if any(word in description_lower for word in ["code", "programming", "function", "class"]):
        analysis["domain"] = "code_analysis"
    elif any(word in description_lower for word in ["architecture", "design", "structure", "system"]):
        analysis["domain"] = "architecture"
    elif any(word in description_lower for word in ["debug", "error", "bug", "issue"]):
        analysis["domain"] = "debugging"

    # Analyze strategy
    if any(word in description_lower for word in ["step", "sequential", "process"]):
        analysis["recommended_strategy"] = "chain_of_thought"
    elif any(word in description_lower for word in ["iterative", "refine", "improve"]):
        analysis["recommended_strategy"] = "iterative"

    return analysis


def generate_optimized_parameters(
    task_analysis: Dict[str, Any],
    performance_target: str,
    constraints: Dict[str, Any]
) -> Dict[str, Any]:
    """Generate optimized parameters for reasoning"""
    params = {
        "complexity": task_analysis["complexity"],
        "domain": task_analysis["domain"],
        "strategy": task_analysis["recommended_strategy"],
        "max_steps": task_analysis["estimated_steps"],
        "timeout": 300
    }

    # Adjust based on performance target
    if performance_target == "speed":
        params["max_steps"] = min(params["max_steps"], 5)
        params["timeout"] = 60
        params["strategy"] = "hierarchical"
    elif performance_target == "accuracy":
        params["max_steps"] = max(params["max_steps"], 8)
        params["timeout"] = 600
        params["strategy"] = "self_correction"
    elif performance_target == "comprehensiveness":
        params["max_steps"] = max(params["max_steps"], 15)
        params["timeout"] = 900
        params["strategy"] = "iterative"

    # Apply constraints
    if "max_time" in constraints:
        params["timeout"] = min(params["timeout"], constraints["max_time"])
    if "max_steps" in constraints:
        params["max_steps"] = min(params["max_steps"], constraints["max_steps"])

    return params


def generate_optimization_recommendations(
    task_analysis: Dict[str, Any],
    performance_target: str,
    constraints: Dict[str, Any]
) -> List[str]:
    """Generate optimization recommendations"""
    recommendations = []

    # Domain-specific recommendations
    if task_analysis["domain"] == "code_analysis":
        recommendations.append("Use qwen2.5-coder:7b model for better code understanding")
    elif task_analysis["domain"] == "architecture":
        recommendations.append("Use codellama:7b-instruct model for architectural reasoning")

    # Performance target recommendations
    if performance_target == "speed":
        recommendations.append("Consider reducing max_steps for faster execution")
        recommendations.append("Use hierarchical strategy for efficiency")
    elif performance_target == "accuracy":
        recommendations.append("Enable self-correction for better accuracy")
        recommendations.append("Increase timeout to allow thorough analysis")

    # Constraint-based recommendations
    if constraints.get("max_time") and constraints["max_time"] < 300:
        recommendations.append("Consider using lower complexity setting due to time constraints")

    return recommendations


async def main():
    """Main function to run the MCP server"""
    # Initialize server
    async with stdio_server() as (read_stream, write_stream):
        await app.run(
            read_stream,
            write_stream,
            InitializationOptions(
                server_name="sequential-thinking",
                server_version="1.0.0",
                capabilities=app.get_capabilities(
                    notification_options=NotificationOptions(),
                    experimental_capabilities={}
                )
            )
        )


if __name__ == "__main__":
    asyncio.run(main())

