#!/usr/bin/env node

import { Server } from "@modelcontextprotocol/sdk/server/index.js";
import { StdioServerTransport } from "@modelcontextprotocol/sdk/server/stdio.js";
import {
    CallToolRequestSchema,
    ListToolsRequestSchema
} from "@modelcontextprotocol/sdk/types.js";
import { promises as fs } from 'fs';
import path from 'path';

interface Entity {
    name: string;
    entityType: string;
    observations: string[];
}

interface Relation {
    from: string;
    to: string;
    relationType: string;
}

interface KnowledgeGraph {
    entities: Record<string, Entity>;
    relations: Relation[];
}

interface MemoryServerState {
    graph: KnowledgeGraph;
    memoryFilePath: string;
}

class MemoryServer {
    private state: MemoryServerState;
    private server: Server;

    constructor() {
        const memoryFilePath = process.env.MEMORY_FILE_PATH || path.join(process.cwd(), 'memory.json');

        this.state = {
            graph: {
                entities: {},
                relations: []
            },
            memoryFilePath
        };

        this.server = new Server(
            {
                name: "memory-server",
                version: "1.0.0",
            }
        );

        this.setupToolHandlers();
        this.loadMemory();
    }

    private async loadMemory(): Promise<void> {
        try {
            const data = await fs.readFile(this.state.memoryFilePath, 'utf-8');
            this.state.graph = JSON.parse(data);
            console.error(`[Memory Server] Loaded memory from ${this.state.memoryFilePath}`);
        } catch (error) {
            console.error(`[Memory Server] No existing memory file found, starting fresh`);
            await this.saveMemory();
        }
    }

    private async saveMemory(): Promise<void> {
        try {
            await fs.writeFile(this.state.memoryFilePath, JSON.stringify(this.state.graph, null, 2));
            console.error(`[Memory Server] Saved memory to ${this.state.memoryFilePath}`);
        } catch (error) {
            console.error(`[Memory Server] Error saving memory: ${error}`);
        }
    }

    private setupToolHandlers(): void {
        this.server.setRequestHandler(ListToolsRequestSchema, async () => {
            return {
                tools: [
                    {
                        name: "create_entities",
                        description: "Create multiple new entities in the knowledge graph",
                        inputSchema: {
                            type: "object",
                            properties: {
                                entities: {
                                    type: "array",
                                    items: {
                                        type: "object",
                                        properties: {
                                            name: { type: "string" },
                                            entityType: { type: "string" },
                                            observations: {
                                                type: "array",
                                                items: { type: "string" }
                                            }
                                        },
                                        required: ["name", "entityType"]
                                    }
                                }
                            },
                            required: ["entities"]
                        }
                    },
                    {
                        name: "create_relations",
                        description: "Create multiple new relations between entities",
                        inputSchema: {
                            type: "object",
                            properties: {
                                relations: {
                                    type: "array",
                                    items: {
                                        type: "object",
                                        properties: {
                                            from: { type: "string" },
                                            to: { type: "string" },
                                            relationType: { type: "string" }
                                        },
                                        required: ["from", "to", "relationType"]
                                    }
                                }
                            },
                            required: ["relations"]
                        }
                    },
                    {
                        name: "add_observations",
                        description: "Add new observations to existing entities",
                        inputSchema: {
                            type: "object",
                            properties: {
                                observations: {
                                    type: "array",
                                    items: {
                                        type: "object",
                                        properties: {
                                            entityName: { type: "string" },
                                            contents: {
                                                type: "array",
                                                items: { type: "string" }
                                            }
                                        },
                                        required: ["entityName", "contents"]
                                    }
                                }
                            },
                            required: ["observations"]
                        }
                    },
                    {
                        name: "delete_entities",
                        description: "Remove entities and their relations",
                        inputSchema: {
                            type: "object",
                            properties: {
                                entityNames: {
                                    type: "array",
                                    items: { type: "string" }
                                }
                            },
                            required: ["entityNames"]
                        }
                    },
                    {
                        name: "delete_observations",
                        description: "Remove specific observations from entities",
                        inputSchema: {
                            type: "object",
                            properties: {
                                deletions: {
                                    type: "array",
                                    items: {
                                        type: "object",
                                        properties: {
                                            entityName: { type: "string" },
                                            observations: {
                                                type: "array",
                                                items: { type: "string" }
                                            }
                                        },
                                        required: ["entityName", "observations"]
                                    }
                                }
                            },
                            required: ["deletions"]
                        }
                    },
                    {
                        name: "delete_relations",
                        description: "Remove specific relations from the graph",
                        inputSchema: {
                            type: "object",
                            properties: {
                                relations: {
                                    type: "array",
                                    items: {
                                        type: "object",
                                        properties: {
                                            from: { type: "string" },
                                            to: { type: "string" },
                                            relationType: { type: "string" }
                                        },
                                        required: ["from", "to", "relationType"]
                                    }
                                }
                            },
                            required: ["relations"]
                        }
                    },
                    {
                        name: "read_graph",
                        description: "Read the entire knowledge graph",
                        inputSchema: {
                            type: "object",
                            properties: {}
                        }
                    },
                    {
                        name: "search_nodes",
                        description: "Search for nodes based on query",
                        inputSchema: {
                            type: "object",
                            properties: {
                                query: { type: "string" }
                            },
                            required: ["query"]
                        }
                    },
                    {
                        name: "open_nodes",
                        description: "Retrieve specific nodes by name",
                        inputSchema: {
                            type: "object",
                            properties: {
                                names: {
                                    type: "array",
                                    items: { type: "string" }
                                }
                            },
                            required: ["names"]
                        }
                    }
                ]
            };
        });

        this.server.setRequestHandler(CallToolRequestSchema, async (request) => {
            const { name, arguments: args } = request.params;

            switch (name) {
                case "create_entities":
                    return this.createEntities(args);
                case "create_relations":
                    return this.createRelations(args);
                case "add_observations":
                    return this.addObservations(args);
                case "delete_entities":
                    return this.deleteEntities(args);
                case "delete_observations":
                    return this.deleteObservations(args);
                case "delete_relations":
                    return this.deleteRelations(args);
                case "read_graph":
                    return this.readGraph();
                case "search_nodes":
                    return this.searchNodes(args);
                case "open_nodes":
                    return this.openNodes(args);
                default:
                    throw new Error(`Unknown tool: ${name}`);
            }
        });
    }

    private async createEntities(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { entities } = args;
        const created: string[] = [];
        const skipped: string[] = [];

        for (const entity of entities) {
            if (!this.state.graph.entities[entity.name]) {
                this.state.graph.entities[entity.name] = {
                    name: entity.name,
                    entityType: entity.entityType,
                    observations: entity.observations || []
                };
                created.push(entity.name);
            } else {
                skipped.push(entity.name);
            }
        }

        await this.saveMemory();

        return {
            content: [{
                type: "text",
                text: `Created entities: ${created.join(', ')}\nSkipped (already exist): ${skipped.join(', ')}`
            }]
        };
    }

    private async createRelations(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { relations } = args;
        const created: string[] = [];
        const skipped: string[] = [];

        for (const relation of relations) {
            const exists = this.state.graph.relations.some(r =>
                r.from === relation.from && r.to === relation.to && r.relationType === relation.relationType
            );

            if (!exists) {
                this.state.graph.relations.push(relation);
                created.push(`${relation.from} ${relation.relationType} ${relation.to}`);
            } else {
                skipped.push(`${relation.from} ${relation.relationType} ${relation.to}`);
            }
        }

        await this.saveMemory();

        return {
            content: [{
                type: "text",
                text: `Created relations: ${created.join(', ')}\nSkipped (already exist): ${skipped.join(', ')}`
            }]
        };
    }

    private async addObservations(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { observations } = args;
        const results: string[] = [];
        const errors: string[] = [];

        for (const obs of observations) {
            const entity = this.state.graph.entities[obs.entityName];
            if (entity) {
                entity.observations.push(...obs.contents);
                results.push(`Added ${obs.contents.length} observations to ${obs.entityName}`);
            } else {
                errors.push(`Entity ${obs.entityName} not found`);
            }
        }

        await this.saveMemory();

        return {
            content: [{
                type: "text",
                text: `Results: ${results.join(', ')}\nErrors: ${errors.join(', ')}`
            }]
        };
    }

    private async deleteEntities(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { entityNames } = args;
        const deleted: string[] = [];

        for (const name of entityNames) {
            if (this.state.graph.entities[name]) {
                delete this.state.graph.entities[name];
                // Remove related relations
                this.state.graph.relations = this.state.graph.relations.filter(r =>
                    r.from !== name && r.to !== name
                );
                deleted.push(name);
            }
        }

        await this.saveMemory();

        return {
            content: [{
                type: "text",
                text: `Deleted entities: ${deleted.join(', ')}`
            }]
        };
    }

    private async deleteObservations(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { deletions } = args;
        const results: string[] = [];

        for (const deletion of deletions) {
            const entity = this.state.graph.entities[deletion.entityName];
            if (entity) {
                const beforeCount = entity.observations.length;
                entity.observations = entity.observations.filter(obs =>
                    !deletion.observations.includes(obs)
                );
                const afterCount = entity.observations.length;
                results.push(`Removed ${beforeCount - afterCount} observations from ${deletion.entityName}`);
            }
        }

        await this.saveMemory();

        return {
            content: [{
                type: "text",
                text: `Results: ${results.join(', ')}`
            }]
        };
    }

    private async deleteRelations(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { relations } = args;
        const deleted: string[] = [];

        for (const relation of relations) {
            const index = this.state.graph.relations.findIndex(r =>
                r.from === relation.from && r.to === relation.to && r.relationType === relation.relationType
            );
            if (index !== -1) {
                this.state.graph.relations.splice(index, 1);
                deleted.push(`${relation.from} ${relation.relationType} ${relation.to}`);
            }
        }

        await this.saveMemory();

        return {
            content: [{
                type: "text",
                text: `Deleted relations: ${deleted.join(', ')}`
            }]
        };
    }

    private async readGraph(): Promise<{ content: Array<{ type: string; text: string }> }> {
        return {
            content: [{
                type: "text",
                text: JSON.stringify(this.state.graph, null, 2)
            }]
        };
    }

    private async searchNodes(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { query } = args;
        const queryLower = query.toLowerCase();
        const results: Entity[] = [];

        for (const entity of Object.values(this.state.graph.entities)) {
            if (entity.name.toLowerCase().includes(queryLower) ||
                entity.entityType.toLowerCase().includes(queryLower) ||
                entity.observations.some(obs => obs.toLowerCase().includes(queryLower))) {
                results.push(entity);
            }
        }

        // Get relations for found entities
        const entityNames = results.map(e => e.name);
        const relations = this.state.graph.relations.filter(r =>
            entityNames.includes(r.from) || entityNames.includes(r.to)
        );

        return {
            content: [{
                type: "text",
                text: JSON.stringify({ entities: results, relations }, null, 2)
            }]
        };
    }

    private async openNodes(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const { names } = args;
        const entities: Entity[] = [];
        const relations: Relation[] = [];

        for (const name of names) {
            const entity = this.state.graph.entities[name];
            if (entity) {
                entities.push(entity);
            }
        }

        // Get relations between requested entities
        const entityNames = entities.map(e => e.name);
        relations.push(...this.state.graph.relations.filter(r =>
            entityNames.includes(r.from) && entityNames.includes(r.to)
        ));

        return {
            content: [{
                type: "text",
                text: JSON.stringify({ entities, relations }, null, 2)
            }]
        };
    }

    public async start(): Promise<void> {
        const transport = new StdioServerTransport();
        await this.server.connect(transport);
        console.error('[Memory Server] Started successfully');
    }
}

// Start the server
const server = new MemoryServer();
server.start().catch(console.error);

