#!/usr/bin/env node

import { Server } from "@modelcontextprotocol/sdk/server/index.js";
import { StdioServerTransport } from "@modelcontextprotocol/sdk/server/stdio.js";
import {
    CallToolRequestSchema,
    ListToolsRequestSchema,
    ErrorCode,
    McpError
} from "@modelcontextprotocol/sdk/types.js";

interface ReasoningStep {
    step_number: number;
    description: string;
    reasoning: string;
    confidence: number;
    dependencies: string[];
    status: 'pending' | 'in_progress' | 'completed' | 'failed';
    result?: any;
    error?: string;
}

interface SequentialThinkingResult {
    success: boolean;
    task_id: string;
    steps: ReasoningStep[];
    final_output: any;
    confidence: number;
    quality_score: number;
    total_time: number;
    strategy_used: string;
    recommendations: string[];
}

interface DecompositionResult {
    task_id: string;
    description: string;
    domain: string;
    complexity: string;
    strategy: string;
    steps: ReasoningStep[];
    total_steps: number;
}

class SequentialThinkingServer {
    private server: Server;
    private tasks: Map<string, SequentialThinkingResult> = new Map();

    constructor() {
        this.server = new Server(
            {
                name: "sequential-thinking",
                version: "1.0.0",
            }
        );

        this.setupToolHandlers();
    }

    private setupToolHandlers(): void {
        this.server.setRequestHandler(ListToolsRequestSchema, async () => {
            return {
                tools: [
                    {
                        name: "analyze_problem",
                        description: "Analyze a problem using sequential thinking and step-by-step reasoning",
                        inputSchema: {
                            type: "object",
                            properties: {
                                description: {
                                    type: "string",
                                    description: "Description of the problem to solve"
                                },
                                domain: {
                                    type: "string",
                                    description: "Domain of the problem (code_analysis, architecture, debugging, etc.)",
                                    enum: ["general", "code_analysis", "architecture", "debugging", "refactoring", "optimization", "security", "testing"],
                                    default: "general"
                                },
                                complexity: {
                                    type: "string",
                                    description: "Complexity level of the task",
                                    enum: ["low", "medium", "high", "critical"],
                                    default: "medium"
                                },
                                strategy: {
                                    type: "string",
                                    description: "Reasoning strategy to use",
                                    enum: ["hierarchical", "iterative", "chain_of_thought", "self_correction", "parallel"],
                                    default: "hierarchical"
                                },
                                constraints: {
                                    type: "array",
                                    items: { type: "string" },
                                    description: "List of constraints for the task",
                                    default: []
                                },
                                context: {
                                    type: "object",
                                    description: "Additional context for the task",
                                    default: {}
                                },
                                max_steps: {
                                    type: "integer",
                                    description: "Maximum number of reasoning steps",
                                    minimum: 1,
                                    maximum: 50,
                                    default: 10
                                }
                            },
                            required: ["description"]
                        }
                    },
                    {
                        name: "decompose_problem",
                        description: "Decompose a complex problem into logical steps without executing them",
                        inputSchema: {
                            type: "object",
                            properties: {
                                description: {
                                    type: "string",
                                    description: "Description of the problem to decompose"
                                },
                                domain: {
                                    type: "string",
                                    description: "Domain of the problem",
                                    enum: ["general", "code_analysis", "architecture", "debugging", "refactoring", "optimization", "security", "testing"],
                                    default: "general"
                                },
                                complexity: {
                                    type: "string",
                                    description: "Complexity level of the task",
                                    enum: ["low", "medium", "high", "critical"],
                                    default: "medium"
                                },
                                strategy: {
                                    type: "string",
                                    description: "Decomposition strategy to use",
                                    enum: ["hierarchical", "iterative", "chain_of_thought", "self_correction", "parallel"],
                                    default: "hierarchical"
                                },
                                max_steps: {
                                    type: "integer",
                                    description: "Maximum number of steps to generate",
                                    minimum: 1,
                                    maximum: 50,
                                    default: 10
                                }
                            },
                            required: ["description"]
                        }
                    },
                    {
                        name: "execute_step",
                        description: "Execute a specific reasoning step with given context",
                        inputSchema: {
                            type: "object",
                            properties: {
                                step_description: {
                                    type: "string",
                                    description: "Description of the step to execute"
                                },
                                context: {
                                    type: "object",
                                    description: "Context for step execution",
                                    default: {}
                                },
                                domain: {
                                    type: "string",
                                    description: "Domain of the step",
                                    enum: ["general", "code_analysis", "architecture", "debugging", "refactoring", "optimization", "security", "testing"],
                                    default: "general"
                                },
                                previous_steps: {
                                    type: "array",
                                    items: { type: "object" },
                                    description: "Previous steps for context",
                                    default: []
                                }
                            },
                            required: ["step_description"]
                        }
                    },
                    {
                        name: "validate_reasoning",
                        description: "Validate the quality and correctness of reasoning results",
                        inputSchema: {
                            type: "object",
                            properties: {
                                reasoning_result: {
                                    type: "object",
                                    description: "Reasoning result to validate",
                                    properties: {
                                        steps: {
                                            type: "array",
                                            items: { type: "object" }
                                        },
                                        final_output: {
                                            type: "object"
                                        },
                                        confidence: {
                                            type: "number",
                                            minimum: 0,
                                            maximum: 1
                                        }
                                    },
                                    required: ["steps", "final_output"]
                                },
                                validation_criteria: {
                                    type: "array",
                                    items: { type: "string" },
                                    description: "Specific criteria to validate against",
                                    default: ["logical_consistency", "completeness", "accuracy"]
                                }
                            },
                            required: ["reasoning_result"]
                        }
                    },
                    {
                        name: "get_reasoning_tips",
                        description: "Get tips and best practices for different types of reasoning tasks",
                        inputSchema: {
                            type: "object",
                            properties: {
                                domain: {
                                    type: "string",
                                    description: "Domain to get tips for",
                                    enum: ["general", "code_analysis", "architecture", "debugging", "refactoring", "optimization", "security", "testing"],
                                    default: "general"
                                },
                                complexity: {
                                    type: "string",
                                    description: "Complexity level",
                                    enum: ["low", "medium", "high", "critical"],
                                    default: "medium"
                                }
                            }
                        }
                    }
                ]
            };
        });

        this.server.setRequestHandler(CallToolRequestSchema, async (request) => {
            const { name, arguments: args } = request.params;

            try {
                switch (name) {
                    case "analyze_problem":
                        return await this.analyzeProblem(args);
                    case "decompose_problem":
                        return await this.decomposeProblem(args);
                    case "execute_step":
                        return await this.executeStep(args);
                    case "validate_reasoning":
                        return await this.validateReasoning(args);
                    case "get_reasoning_tips":
                        return await this.getReasoningTips(args);
                    default:
                        throw new McpError(ErrorCode.MethodNotFound, `Unknown tool: ${name}`);
                }
            } catch (error) {
                if (error instanceof McpError) {
                    throw error;
                }
                throw new McpError(ErrorCode.InternalError, `Tool execution failed: ${error}`);
            }
        });
    }

    private async analyzeProblem(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const {
            description,
            domain = "general",
            complexity = "medium",
            strategy = "hierarchical",
            constraints = [],
            context = {},
            max_steps = 10
        } = args;

        if (!description) {
            throw new McpError(ErrorCode.InvalidParams, "Description is required");
        }

        // Generate a unique task ID
        const task_id = `task_${Date.now()}_${Math.random().toString(36).substring(2, 15)}`;

        // Simulate sequential thinking analysis
        const steps = await this.generateReasoningSteps(description, domain, complexity, strategy, max_steps);

        // Execute the steps
        const executedSteps = await this.executeSteps(steps, context);

        // Generate final output
        const finalOutput = this.generateFinalOutput(executedSteps, description, domain);

        // Calculate metrics
        const confidence = this.calculateConfidence(executedSteps);
        const qualityScore = this.calculateQualityScore(executedSteps, constraints);
        const recommendations = this.generateRecommendations(executedSteps, domain, complexity);

        const result: SequentialThinkingResult = {
            success: true,
            task_id,
            steps: executedSteps,
            final_output: finalOutput,
            confidence,
            quality_score: qualityScore,
            total_time: executedSteps.reduce((sum, step) => sum + 0.5, 0), // Simulated time
            strategy_used: strategy,
            recommendations
        };

        // Store the result
        this.tasks.set(task_id, result);

        return {
            content: [{
                type: "text",
                text: JSON.stringify(result, null, 2)
            }]
        };
    }

    private async decomposeProblem(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const {
            description,
            domain = "general",
            complexity = "medium",
            strategy = "hierarchical",
            max_steps = 10
        } = args;

        if (!description) {
            throw new McpError(ErrorCode.InvalidParams, "Description is required");
        }

        const task_id = `decomp_${Date.now()}_${Math.random().toString(36).substring(2, 15)}`;
        const steps = await this.generateReasoningSteps(description, domain, complexity, strategy, max_steps);

        const result: DecompositionResult = {
            task_id,
            description,
            domain,
            complexity,
            strategy,
            steps: steps.map(step => ({ ...step, status: 'pending' as const })),
            total_steps: steps.length
        };

        return {
            content: [{
                type: "text",
                text: JSON.stringify(result, null, 2)
            }]
        };
    }

    private async executeStep(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const {
            step_description,
            context = {},
            domain = "general",
            previous_steps = []
        } = args;

        if (!step_description) {
            throw new McpError(ErrorCode.InvalidParams, "Step description is required");
        }

        // Simulate step execution
        const step: ReasoningStep = {
            step_number: previous_steps.length + 1,
            description: step_description,
            reasoning: this.generateStepReasoning(step_description, domain, context),
            confidence: 0.8 + Math.random() * 0.2,
            dependencies: [],
            status: 'completed',
            result: this.generateStepResult(step_description, domain, context)
        };

        return {
            content: [{
                type: "text",
                text: JSON.stringify(step, null, 2)
            }]
        };
    }

    private async validateReasoning(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const {
            reasoning_result,
            validation_criteria = ["logical_consistency", "completeness", "accuracy"]
        } = args;

        if (!reasoning_result) {
            throw new McpError(ErrorCode.InvalidParams, "Reasoning result is required");
        }

        const { steps = [], final_output = {}, confidence = 0 } = reasoning_result;

        // Validate according to criteria
        const validationResults: any = {};
        let overallScore = 0;

        for (const criterion of validation_criteria) {
            let score = 0;

            switch (criterion) {
                case "logical_consistency":
                    score = this.validateLogicalConsistency(steps);
                    break;
                case "completeness":
                    score = this.validateCompleteness(steps, final_output);
                    break;
                case "accuracy":
                    score = this.validateAccuracy(steps, confidence);
                    break;
                default:
                    score = 0.7; // Default score for unknown criteria
            }

            validationResults[criterion] = {
                score,
                description: this.getValidationDescription(criterion)
            };
            overallScore += score;
        }

        overallScore = overallScore / validation_criteria.length;

        const recommendations = this.generateValidationRecommendations(overallScore, confidence, steps.length);

        const result = {
            validation_results: validationResults,
            overall_score: overallScore,
            confidence,
            recommendations,
            validated_criteria: validation_criteria,
            validation_passed: overallScore >= 0.7
        };

        return {
            content: [{
                type: "text",
                text: JSON.stringify(result, null, 2)
            }]
        };
    }

    private async getReasoningTips(args: any): Promise<{ content: Array<{ type: string; text: string }> }> {
        const {
            domain = "general",
            complexity = "medium"
        } = args;

        const tips = {
            general: [
                "Break complex problems into smaller, manageable steps",
                "Always validate your reasoning at each step",
                "Consider multiple approaches before choosing one",
                "Document your assumptions and constraints clearly"
            ],
            code_analysis: [
                "Focus on understanding the code's intent first",
                "Look for patterns, anti-patterns, and potential edge cases",
                "Consider performance, security, and maintainability",
                "Think about testing strategies for the code"
            ],
            architecture: [
                "Start with high-level system design",
                "Consider scalability, reliability, and performance requirements",
                "Think about data flow and component interactions",
                "Plan for future extensibility and maintenance"
            ],
            debugging: [
                "Reproduce the issue consistently",
                "Use systematic elimination to narrow down the problem",
                "Check logs, stack traces, and error messages carefully",
                "Consider recent changes that might have caused the issue"
            ],
            refactoring: [
                "Understand the existing code thoroughly before making changes",
                "Make incremental improvements rather than large rewrites",
                "Ensure comprehensive test coverage before refactoring",
                "Focus on improving readability and maintainability"
            ],
            optimization: [
                "Measure performance before optimizing",
                "Focus on bottlenecks that have the most impact",
                "Consider trade-offs between different optimization approaches",
                "Validate that optimizations actually improve performance"
            ],
            security: [
                "Think like an attacker - consider all possible attack vectors",
                "Apply defense in depth principles",
                "Validate and sanitize all inputs",
                "Keep security considerations throughout the entire process"
            ],
            testing: [
                "Design tests that cover both positive and negative cases",
                "Consider edge cases and boundary conditions",
                "Use appropriate testing strategies (unit, integration, end-to-end)",
                "Ensure tests are maintainable and reliable"
            ]
        };

        const complexityTips = {
            low: [
                "Focus on clarity and simplicity",
                "Don't overthink simple problems",
                "Use straightforward approaches"
            ],
            medium: [
                "Balance thoroughness with efficiency",
                "Consider multiple perspectives",
                "Document your reasoning process"
            ],
            high: [
                "Take time to understand the full scope",
                "Break down into smaller sub-problems",
                "Consider long-term implications"
            ],
            critical: [
                "Be extremely thorough in your analysis",
                "Consider all possible failure modes",
                "Document everything for future reference",
                "Get multiple perspectives when possible"
            ]
        };

        const result = {
            domain,
            complexity,
            domain_specific_tips: tips[domain as keyof typeof tips] || tips.general,
            complexity_tips: complexityTips[complexity as keyof typeof complexityTips] || complexityTips.medium,
            general_best_practices: [
                "Always question your assumptions",
                "Look for patterns and connections",
                "Consider the context and constraints",
                "Validate your conclusions"
            ]
        };

        return {
            content: [{
                type: "text",
                text: JSON.stringify(result, null, 2)
            }]
        };
    }

    // Helper methods for simulation
    private async generateReasoningSteps(
        description: string,
        domain: string,
        complexity: string,
        strategy: string,
        maxSteps: number
    ): Promise<ReasoningStep[]> {
        const steps: ReasoningStep[] = [];
        const stepCount = Math.min(maxSteps, this.getStepCountForComplexity(complexity));

        for (let i = 1; i <= stepCount; i++) {
            steps.push({
                step_number: i,
                description: this.generateStepDescription(description, domain, i, stepCount),
                reasoning: "",
                confidence: 0.8 + Math.random() * 0.2,
                dependencies: i > 1 ? [`step_${i-1}`] : [],
                status: 'pending'
            });
        }

        return steps;
    }

    private async executeSteps(steps: ReasoningStep[], context: any): Promise<ReasoningStep[]> {
        const executedSteps: ReasoningStep[] = [];

        for (const step of steps) {
            const executedStep = {
                ...step,
                status: 'completed' as const,
                reasoning: this.generateStepReasoning(step.description, "general", context),
                result: this.generateStepResult(step.description, "general", context)
            };
            executedSteps.push(executedStep);
        }

        return executedSteps;
    }

    private generateStepDescription(description: string, domain: string, stepNum: number, totalSteps: number): string {
        const templates = {
            general: [
                "Analyze the problem statement",
                "Identify key requirements and constraints",
                "Consider potential approaches",
                "Evaluate trade-offs and implications",
                "Formulate a solution strategy",
                "Validate the approach"
            ],
            code_analysis: [
                "Review the code structure and organization",
                "Identify potential bugs and issues",
                "Analyze performance implications",
                "Check for security vulnerabilities",
                "Evaluate maintainability and readability",
                "Suggest improvements"
            ],
            architecture: [
                "Define system requirements",
                "Design high-level architecture",
                "Plan component interactions",
                "Consider scalability requirements",
                "Design data flow and storage",
                "Plan for monitoring and deployment"
            ]
        };

        const domainTemplates = templates[domain as keyof typeof templates] || templates.general;
        const templateIndex = Math.min(stepNum - 1, domainTemplates.length - 1);

        return domainTemplates[templateIndex] || `Step ${stepNum}: Continue analysis`;
    }

    private generateStepReasoning(description: string, domain: string, context: any): string {
        return `Analyzing: ${description}. Based on the ${domain} domain context, considering the provided constraints and requirements.`;
    }

    private generateStepResult(description: string, domain: string, context: any): any {
        return {
            findings: `Analysis completed for: ${description}`,
            recommendations: [`Recommendation based on ${domain} analysis`],
            confidence: 0.85
        };
    }

    private generateFinalOutput(steps: ReasoningStep[], description: string, domain: string): any {
        return {
            summary: `Sequential analysis completed for: ${description}`,
            approach: `Applied ${domain} domain expertise`,
            steps_completed: steps.length,
            key_findings: steps.map(step => step.result?.findings || "Analysis completed"),
            recommendations: steps.flatMap(step => step.result?.recommendations || []),
            confidence: this.calculateConfidence(steps)
        };
    }

    private calculateConfidence(steps: ReasoningStep[]): number {
        if (steps.length === 0) return 0;
        return steps.reduce((sum, step) => sum + step.confidence, 0) / steps.length;
    }

    private calculateQualityScore(steps: ReasoningStep[], constraints: string[]): number {
        let score = 0.8; // Base score

        // Bonus for completed steps
        const completedSteps = steps.filter(step => step.status === 'completed').length;
        const completionRate = completedSteps / steps.length;
        score += completionRate * 0.2;

        // Bonus for considering constraints
        if (constraints.length > 0) {
            score += 0.1;
        }

        return Math.min(1.0, score);
    }

    private generateRecommendations(steps: ReasoningStep[], domain: string, complexity: string): string[] {
        const recommendations = [];

        if (steps.length < 3) {
            recommendations.push("Consider breaking down the problem into more detailed steps");
        }

        if (domain === "code_analysis") {
            recommendations.push("Consider adding automated testing for the analyzed code");
        }

        if (complexity === "high") {
            recommendations.push("Consider peer review for high-complexity tasks");
        }

        return recommendations;
    }

    private getStepCountForComplexity(complexity: string): number {
        switch (complexity) {
            case "low": return 3;
            case "medium": return 5;
            case "high": return 8;
            case "critical": return 12;
            default: return 5;
        }
    }

    private validateLogicalConsistency(steps: any[]): number {
        if (steps.length === 0) return 0;

        let score = 1.0;

        // Check for failed steps
        const failedSteps = steps.filter((step: any) => step.status === 'failed');
        score -= failedSteps.length * 0.1;

        // Check for logical flow
        for (let i = 1; i < steps.length; i++) {
            const currentStep = steps[i];
            if (!currentStep.dependencies || currentStep.dependencies.length === 0) {
                score -= 0.05; // Penalty for missing dependencies
            }
        }

        return Math.max(0, score);
    }

    private validateCompleteness(steps: any[], finalOutput: any): number {
        if (steps.length === 0 || !finalOutput) return 0;

        let score = 0.5; // Base score

        // Check step completion rate
        const completedSteps = steps.filter((step: any) => step.status === 'completed');
        const completionRate = completedSteps.length / steps.length;
        score += completionRate * 0.3;

        // Check if final output is meaningful
        if (finalOutput.summary || finalOutput.recommendations) {
            score += 0.2;
        }

        return Math.min(1.0, score);
    }

    private validateAccuracy(steps: any[], confidence: number): number {
        if (steps.length === 0) return 0;

        let score = confidence; // Use confidence as base accuracy

        // Adjust based on step quality
        const highConfidenceSteps = steps.filter((step: any) => step.confidence > 0.8);
        if (highConfidenceSteps.length > 0) {
            score += 0.1;
        }

        // Penalty for errors
        const errorSteps = steps.filter((step: any) => step.error);
        if (errorSteps.length > 0) {
            score -= errorSteps.length * 0.1;
        }

        return Math.max(0, Math.min(1.0, score));
    }

    private getValidationDescription(criterion: string): string {
        const descriptions = {
            logical_consistency: "Steps follow logical sequence and dependencies",
            completeness: "All necessary steps are present and executed",
            accuracy: "Steps produce accurate and reliable results"
        };
        return descriptions[criterion as keyof typeof descriptions] || "Validation criterion";
    }

    private generateValidationRecommendations(overallScore: number, confidence: number, stepCount: number): string[] {
        const recommendations = [];

        if (overallScore < 0.7) {
            recommendations.push("Consider increasing step granularity for better analysis");
        }

        if (confidence < 0.8) {
            recommendations.push("Consider using self-correction strategy to improve confidence");
        }

        if (stepCount < 3) {
            recommendations.push("Consider breaking down problem into more detailed steps");
        }

        return recommendations;
    }

    public async start(): Promise<void> {
        const transport = new StdioServerTransport();
        await this.server.connect(transport);
        console.error('[Sequential Thinking Server] Started successfully');
    }
}

// Start the server
const server = new SequentialThinkingServer();
server.start().catch(console.error);

