#!/usr/bin/env node

/**
 * Test script for Sequential Thinking MCP Server
 * This script tests the server functionality by sending JSON-RPC requests
 */

import { spawn } from 'child_process';
import { promises as fs } from 'fs';
import path from 'path';

class MCPTester {
    constructor() {
        this.serverProcess = null;
        this.testResults = [];
    }

    async startServer() {
        return new Promise((resolve, reject) => {
            console.log('ðŸš€ Starting Sequential Thinking MCP Server...');

            this.serverProcess = spawn('node', ['./dist/index.js'], {
                stdio: ['pipe', 'pipe', 'pipe'],
                cwd: process.cwd()
            });

            this.serverProcess.stderr.on('data', (data) => {
                const message = data.toString();
                if (message.includes('Started successfully')) {
                    console.log('âœ… Server started successfully');
                    resolve();
                } else {
                    console.error('Server stderr:', message);
                }
            });

            this.serverProcess.on('error', (error) => {
                console.error('âŒ Failed to start server:', error);
                reject(error);
            });

            // Give server time to start
            setTimeout(resolve, 2000);
        });
    }

    async stopServer() {
        if (this.serverProcess) {
            this.serverProcess.kill();
            console.log('ðŸ›‘ Server stopped');
        }
    }

    async sendRequest(method, params = {}) {
        return new Promise((resolve, reject) => {
            const request = {
                jsonrpc: '2.0',
                id: Date.now(),
                method,
                params
            };

            const requestString = JSON.stringify(request) + '\n';

            let responseData = '';

            const onData = (data) => {
                responseData += data.toString();
                try {
                    const response = JSON.parse(responseData);
                    this.serverProcess.stdout.off('data', onData);
                    resolve(response);
                } catch (e) {
                    // Continue collecting data
                }
            };

            this.serverProcess.stdout.on('data', onData);
            this.serverProcess.stdin.write(requestString);

            // Timeout after 10 seconds
            setTimeout(() => {
                this.serverProcess.stdout.off('data', onData);
                reject(new Error('Request timeout'));
            }, 10000);
        });
    }

    async testListTools() {
        console.log('\\nðŸ” Testing list_tools...');
        try {
            const response = await this.sendRequest('tools/list');

            if (response.result && response.result.tools) {
                console.log(`âœ… Found ${response.result.tools.length} tools:`);
                response.result.tools.forEach(tool => {
                    console.log(`  - ${tool.name}: ${tool.description}`);
                });
                this.testResults.push({ test: 'list_tools', passed: true });
                return true;
            } else {
                console.log('âŒ No tools found in response');
                this.testResults.push({ test: 'list_tools', passed: false });
                return false;
            }
        } catch (error) {
            console.log('âŒ list_tools failed:', error.message);
            this.testResults.push({ test: 'list_tools', passed: false, error: error.message });
            return false;
        }
    }

    async testAnalyzeProblem() {
        console.log('\\nðŸ§  Testing analyze_problem...');
        try {
            const response = await this.sendRequest('tools/call', {
                name: 'analyze_problem',
                arguments: {
                    description: 'How can I optimize a slow database query?',
                    domain: 'optimization',
                    complexity: 'medium',
                    strategy: 'hierarchical',
                    constraints: ['Maintain data accuracy', 'Minimize resource usage'],
                    max_steps: 5
                }
            });

            if (response.result && response.result.content) {
                const content = JSON.parse(response.result.content[0].text);
                console.log(`âœ… Analysis completed:`);
                console.log(`  - Success: ${content.success}`);
                console.log(`  - Steps: ${content.steps.length}`);
                console.log(`  - Confidence: ${content.confidence.toFixed(2)}`);
                console.log(`  - Quality Score: ${content.quality_score.toFixed(2)}`);
                this.testResults.push({ test: 'analyze_problem', passed: true });
                return true;
            } else {
                console.log('âŒ Invalid response format');
                this.testResults.push({ test: 'analyze_problem', passed: false });
                return false;
            }
        } catch (error) {
            console.log('âŒ analyze_problem failed:', error.message);
            this.testResults.push({ test: 'analyze_problem', passed: false, error: error.message });
            return false;
        }
    }

    async testDecomposeProblem() {
        console.log('\\nðŸ” Testing decompose_problem...');
        try {
            const response = await this.sendRequest('tools/call', {
                name: 'decompose_problem',
                arguments: {
                    description: 'Design a RESTful API for a social media application',
                    domain: 'architecture',
                    complexity: 'high',
                    strategy: 'hierarchical',
                    max_steps: 8
                }
            });

            if (response.result && response.result.content) {
                const content = JSON.parse(response.result.content[0].text);
                console.log(`âœ… Decomposition completed:`);
                console.log(`  - Total steps: ${content.total_steps}`);
                console.log(`  - Strategy: ${content.strategy}`);
                console.log(`  - Domain: ${content.domain}`);
                content.steps.forEach((step, index) => {
                    console.log(`    ${index + 1}. ${step.description}`);
                });
                this.testResults.push({ test: 'decompose_problem', passed: true });
                return true;
            } else {
                console.log('âŒ Invalid response format');
                this.testResults.push({ test: 'decompose_problem', passed: false });
                return false;
            }
        } catch (error) {
            console.log('âŒ decompose_problem failed:', error.message);
            this.testResults.push({ test: 'decompose_problem', passed: false, error: error.message });
            return false;
        }
    }

    async testExecuteStep() {
        console.log('\\nâš¡ Testing execute_step...');
        try {
            const response = await this.sendRequest('tools/call', {
                name: 'execute_step',
                arguments: {
                    step_description: 'Analyze the current code structure and identify potential performance bottlenecks',
                    domain: 'code_analysis',
                    context: {
                        language: 'Python',
                        framework: 'Django'
                    }
                }
            });

            if (response.result && response.result.content) {
                const content = JSON.parse(response.result.content[0].text);
                console.log(`âœ… Step executed:`);
                console.log(`  - Description: ${content.description}`);
                console.log(`  - Status: ${content.status}`);
                console.log(`  - Confidence: ${content.confidence.toFixed(2)}`);
                console.log(`  - Result: ${JSON.stringify(content.result, null, 2)}`);
                this.testResults.push({ test: 'execute_step', passed: true });
                return true;
            } else {
                console.log('âŒ Invalid response format');
                this.testResults.push({ test: 'execute_step', passed: false });
                return false;
            }
        } catch (error) {
            console.log('âŒ execute_step failed:', error.message);
            this.testResults.push({ test: 'execute_step', passed: false, error: error.message });
            return false;
        }
    }

    async testValidateReasoning() {
        console.log('\\nâœ… Testing validate_reasoning...');
        try {
            const mockReasoningResult = {
                steps: [
                    { step_number: 1, description: 'Step 1', status: 'completed', confidence: 0.9 },
                    { step_number: 2, description: 'Step 2', status: 'completed', confidence: 0.8 }
                ],
                final_output: { summary: 'Analysis complete', recommendations: ['Optimize queries'] },
                confidence: 0.85
            };

            const response = await this.sendRequest('tools/call', {
                name: 'validate_reasoning',
                arguments: {
                    reasoning_result: mockReasoningResult,
                    validation_criteria: ['logical_consistency', 'completeness', 'accuracy']
                }
            });

            if (response.result && response.result.content) {
                const content = JSON.parse(response.result.content[0].text);
                console.log(`âœ… Validation completed:`);
                console.log(`  - Overall Score: ${content.overall_score.toFixed(2)}`);
                console.log(`  - Validation Passed: ${content.validation_passed}`);
                console.log(`  - Recommendations: ${content.recommendations.join(', ')}`);
                this.testResults.push({ test: 'validate_reasoning', passed: true });
                return true;
            } else {
                console.log('âŒ Invalid response format');
                this.testResults.push({ test: 'validate_reasoning', passed: false });
                return false;
            }
        } catch (error) {
            console.log('âŒ validate_reasoning failed:', error.message);
            this.testResults.push({ test: 'validate_reasoning', passed: false, error: error.message });
            return false;
        }
    }

    async testGetReasoningTips() {
        console.log('\\nðŸ’¡ Testing get_reasoning_tips...');
        try {
            const response = await this.sendRequest('tools/call', {
                name: 'get_reasoning_tips',
                arguments: {
                    domain: 'code_analysis',
                    complexity: 'high'
                }
            });

            if (response.result && response.result.content) {
                const content = JSON.parse(response.result.content[0].text);
                console.log(`âœ… Tips retrieved:`);
                console.log(`  - Domain: ${content.domain}`);
                console.log(`  - Complexity: ${content.complexity}`);
                console.log(`  - Domain Tips: ${content.domain_specific_tips.length} items`);
                console.log(`  - Complexity Tips: ${content.complexity_tips.length} items`);
                this.testResults.push({ test: 'get_reasoning_tips', passed: true });
                return true;
            } else {
                console.log('âŒ Invalid response format');
                this.testResults.push({ test: 'get_reasoning_tips', passed: false });
                return false;
            }
        } catch (error) {
            console.log('âŒ get_reasoning_tips failed:', error.message);
            this.testResults.push({ test: 'get_reasoning_tips', passed: false, error: error.message });
            return false;
        }
    }

    async runAllTests() {
        console.log('ðŸš€ Starting Sequential Thinking MCP Server Tests');
        console.log('=' * 60);

        try {
            await this.startServer();

            // Run all tests
            await this.testListTools();
            await this.testAnalyzeProblem();
            await this.testDecomposeProblem();
            await this.testExecuteStep();
            await this.testValidateReasoning();
            await this.testGetReasoningTips();

            // Summary
            console.log('\\n' + '=' * 60);
            console.log('ðŸ“Š Test Results Summary');
            console.log('=' * 60);

            const passed = this.testResults.filter(r => r.passed).length;
            const total = this.testResults.length;

            this.testResults.forEach(result => {
                const status = result.passed ? 'âœ… PASSED' : 'âŒ FAILED';
                console.log(`${result.test}: ${status}`);
                if (result.error) {
                    console.log(`  Error: ${result.error}`);
                }
            });

            console.log(`\\nðŸŽ¯ Overall: ${passed}/${total} tests passed`);

            if (passed === total) {
                console.log('ðŸŽ‰ All tests passed! Sequential Thinking MCP Server is ready for use.');
                return true;
            } else {
                console.log('âš ï¸  Some tests failed. Please check the logs for details.');
                return false;
            }

        } catch (error) {
            console.error('âŒ Test suite failed:', error);
            return false;
        } finally {
            await this.stopServer();
        }
    }
}

// Run the tests
const tester = new MCPTester();
tester.runAllTests().then(success => {
    process.exit(success ? 0 : 1);
}).catch(error => {
    console.error('Test suite crashed:', error);
    process.exit(1);
});

