#!/usr/bin/env node

/**
 * Demo script for Sequential Thinking MCP Server
 * Shows how to use the server for complex multi-step problems
 */

import { spawn } from 'child_process';

class SequentialThinkingDemo {
    constructor() {
        this.serverProcess = null;
    }

    async startServer() {
        return new Promise((resolve, reject) => {
            console.log('ðŸš€ Starting Sequential Thinking MCP Server...');

            this.serverProcess = spawn('node', ['./dist/index.js'], {
                stdio: ['pipe', 'pipe', 'pipe'],
                cwd: process.cwd()
            });

            this.serverProcess.stderr.on('data', (data) => {
                const message = data.toString();
                if (message.includes('Started successfully')) {
                    console.log('âœ… Server started successfully\\n');
                    resolve();
                }
            });

            this.serverProcess.on('error', (error) => {
                console.error('âŒ Failed to start server:', error);
                reject(error);
            });

            setTimeout(resolve, 2000);
        });
    }

    async stopServer() {
        if (this.serverProcess) {
            this.serverProcess.kill();
            console.log('\\nðŸ›‘ Server stopped');
        }
    }

    async sendRequest(method, params = {}) {
        return new Promise((resolve, reject) => {
            const request = {
                jsonrpc: '2.0',
                id: Date.now(),
                method,
                params
            };

            const requestString = JSON.stringify(request) + '\\n';

            let responseData = '';

            const onData = (data) => {
                responseData += data.toString();
                try {
                    const response = JSON.parse(responseData);
                    this.serverProcess.stdout.off('data', onData);
                    resolve(response);
                } catch (e) {
                    // Continue collecting data
                }
            };

            this.serverProcess.stdout.on('data', onData);
            this.serverProcess.stdin.write(requestString);

            setTimeout(() => {
                this.serverProcess.stdout.off('data', onData);
                reject(new Error('Request timeout'));
            }, 10000);
        });
    }

    async demoComplexArchitectureProblem() {
        console.log('ðŸ—ï¸  DEMO: Complex Architecture Problem');
        console.log('=' * 50);

        const problem = {
            description: "Design a scalable microservices architecture for a social media platform that needs to handle 1 million users, with features like user profiles, posts, comments, likes, real-time notifications, and content moderation.",
            domain: "architecture",
            complexity: "high",
            strategy: "hierarchical",
            constraints: [
                "Must handle 1M+ concurrent users",
                "Must support real-time features",
                "Must be cost-effective",
                "Must comply with data privacy regulations",
                "Must support global deployment"
            ],
            context: {
                "target_users": 1000000,
                "features": ["user_profiles", "posts", "comments", "likes", "notifications", "moderation"],
                "budget": "startup",
                "timeline": "6 months"
            },
            max_steps: 10
        };

        console.log('ðŸ“ Problem Description:');
        console.log(problem.description);
        console.log('\\nðŸŽ¯ Constraints:');
        problem.constraints.forEach(constraint => console.log(`  - ${constraint}`));

        console.log('\\nðŸ” Starting Analysis...');

        try {
            const response = await this.sendRequest('tools/call', {
                name: 'analyze_problem',
                arguments: problem
            });

            if (response.result && response.result.content) {
                const analysis = JSON.parse(response.result.content[0].text);

                console.log('\\nðŸ“Š Analysis Results:');
                console.log(`âœ… Success: ${analysis.success}`);
                console.log(`ðŸŽ¯ Confidence: ${(analysis.confidence * 100).toFixed(1)}%`);
                console.log(`â­ Quality Score: ${(analysis.quality_score * 100).toFixed(1)}%`);
                console.log(`â±ï¸  Total Time: ${analysis.total_time}s`);
                console.log(`ðŸ§  Strategy Used: ${analysis.strategy_used}`);

                console.log('\\nðŸ“‹ Sequential Steps:');
                analysis.steps.forEach((step, index) => {
                    const status = step.status === 'completed' ? 'âœ…' : 'âŒ';
                    console.log(`${status} Step ${step.step_number}: ${step.description}`);
                    console.log(`   Confidence: ${(step.confidence * 100).toFixed(1)}%`);
                    if (step.result && step.result.findings) {
                        console.log(`   Findings: ${step.result.findings}`);
                    }
                });

                console.log('\\nðŸŽ¯ Final Output:');
                console.log(`Summary: ${analysis.final_output.summary}`);
                console.log(`Approach: ${analysis.final_output.approach}`);
                console.log(`Steps Completed: ${analysis.final_output.steps_completed}`);

                console.log('\\nðŸ’¡ Key Findings:');
                analysis.final_output.key_findings.forEach((finding, index) => {
                    console.log(`  ${index + 1}. ${finding}`);
                });

                console.log('\\nðŸ”® Recommendations:');
                analysis.recommendations.forEach((rec, index) => {
                    console.log(`  ${index + 1}. ${rec}`);
                });

                return analysis;
            }
        } catch (error) {
            console.error('âŒ Analysis failed:', error.message);
            return null;
        }
    }

    async demoCodeOptimizationProblem() {
        console.log('\\n\\nðŸ’» DEMO: Code Optimization Problem');
        console.log('=' * 50);

        const problem = {
            description: "Optimize this Python function that processes large datasets: \\n\\n```python\\ndef process_user_data(users):\\n    results = []\\n    for user in users:\\n        user_posts = []\\n        for post in all_posts:\\n            if post.user_id == user.id:\\n                user_posts.append(post)\\n        user_data = {\\n            'id': user.id,\\n            'name': user.name,\\n            'posts': user_posts,\\n            'post_count': len(user_posts)\\n        }\\n        results.append(user_data)\\n    return results\\n```",
            domain: "optimization",
            complexity: "medium",
            strategy: "iterative",
            constraints: [
                "Maintain data accuracy",
                "Reduce time complexity",
                "Minimize memory usage",
                "Keep code readable"
            ],
            context: {
                "language": "Python",
                "dataset_size": "1M+ records",
                "current_performance": "O(n*m) time complexity"
            },
            max_steps: 6
        };

        console.log('ðŸ“ Problem: Code Optimization');
        console.log('ðŸŽ¯ Target: Optimize nested loop performance');

