            const response = await this.sendRequest('tools/call', {
                name: 'analyze_problem',
                arguments: problem
            });

            if (response.result && response.result.content) {
                const analysis = JSON.parse(response.result.content[0].text);

                console.log('\\nðŸ“Š Optimization Analysis:');
                console.log(`âœ… Success: ${analysis.success}`);
                console.log(`ðŸŽ¯ Confidence: ${(analysis.confidence * 100).toFixed(1)}%`);
                console.log(`â­ Quality Score: ${(analysis.quality_score * 100).toFixed(1)}%`);

                console.log('\\nðŸ”§ Optimization Steps:');
                analysis.steps.forEach((step, index) => {
                    console.log(`${index + 1}. ${step.description} (${(step.confidence * 100).toFixed(1)}%)`);
                });

                return analysis;
            }
        } catch (error) {
            console.error('âŒ Optimization analysis failed:', error.message);
            return null;
        }
    }

    async demoReasoningValidation() {
        console.log('\\n\\nâœ… DEMO: Reasoning Validation');
        console.log('=' * 50);

        const mockReasoning = {
            steps: [
