import { spawn } from 'child_process';

async function testSequentialThinking() {
    console.log('ðŸš€ Testing Sequential Thinking MCP Server');
    console.log('=' * 50);

    // Start the server
    const server = spawn('node', ['./dist/index.js'], {
        stdio: ['pipe', 'pipe', 'pipe'],
        cwd: process.cwd()
    });

    console.log('Server started, waiting for initialization...');

    // Wait for server to start
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Test 1: List tools
    console.log('\n1. Testing tool listing...');
    const listToolsRequest = {
        jsonrpc: '2.0',
        id: 1,
        method: 'tools/list',
        params: {}
    };

    server.stdin.write(JSON.stringify(listToolsRequest) + '\n');

    // Test 2: Analyze a problem
    console.log('\n2. Testing problem analysis...');
    const analyzeRequest = {
        jsonrpc: '2.0',
        id: 2,
        method: 'tools/call',
        params: {
            name: 'analyze_problem',
            arguments: {
                description: 'How can I optimize a Python function that processes large datasets?',
                domain: 'code_analysis',
                complexity: 'medium',
                strategy: 'hierarchical',
                constraints: ['Maintain data accuracy', 'Reduce time complexity'],
                max_steps: 5
            }
        }
    };

    server.stdin.write(JSON.stringify(analyzeRequest) + '\n');

    // Test 3: Get reasoning tips
    console.log('\n3. Testing reasoning tips...');
    const tipsRequest = {
        jsonrpc: '2.0',
        id: 3,
        method: 'tools/call',
        params: {
            name: 'get_reasoning_tips',
            arguments: {
                domain: 'code_analysis',
                complexity: 'high'
            }
        }
    };

    server.stdin.write(JSON.stringify(tipsRequest) + '\n');

    // Listen for responses
    let responseCount = 0;
    server.stdout.on('data', (data) => {
        try {
            const response = JSON.parse(data.toString());
            responseCount++;

            if (response.id === 1) {
                console.log('âœ… Tool listing successful');
                console.log(`   Found ${response.result.tools.length} tools`);
            } else if (response.id === 2) {
                console.log('âœ… Problem analysis successful');
                const analysis = JSON.parse(response.result.content[0].text);
                console.log(`   Confidence: ${(analysis.confidence * 100).toFixed(1)}%`);
                console.log(`   Steps: ${analysis.steps.length}`);
                console.log(`   Quality: ${(analysis.quality_score * 100).toFixed(1)}%`);
            } else if (response.id === 3) {
                console.log('âœ… Reasoning tips successful');
                const tips = JSON.parse(response.result.content[0].text);
                console.log(`   Domain: ${tips.domain}`);
                console.log(`   Complexity: ${tips.complexity}`);
                console.log(`   Tips provided: ${tips.domain_specific_tips.length}`);
            }

            if (responseCount >= 3) {
                console.log('\nðŸŽ‰ All tests completed successfully!');
                console.log('\nðŸ“‹ Summary:');
                console.log('âœ… Sequential Thinking MCP Server is working correctly');
                console.log('âœ… All tools are accessible and functional');
                console.log('âœ… Problem analysis provides detailed step-by-step reasoning');
                console.log('âœ… Domain-specific tips are available');
                console.log('âœ… Ready for Cursor Editor integration');

                server.kill();
                process.exit(0);
            }
        } catch (error) {
            console.error('Error parsing response:', error);
        }
    });

    server.stderr.on('data', (data) => {
        const message = data.toString();
        if (message.includes('Started successfully')) {
            console.log('âœ… Server initialization complete');
        }
    });

    server.on('error', (error) => {
        console.error('âŒ Server error:', error);
        process.exit(1);
    });

    // Timeout after 15 seconds
    setTimeout(() => {
        console.log('âŒ Demo timed out');
        server.kill();
        process.exit(1);
    }, 15000);
}

testSequentialThinking().catch(console.error);

