## ModelContextProtocol Memory Server - Complete Source Code

The memory server is a basic implementation of persistent memory using a local knowledge graph that lets Claude remember information about the user across chats. Here's the complete `index.ts` file:

```typescript
#!/usr/bin/env node

import { Server } from "@modelcontextprotocol/sdk/server/index.js";
import { StdioServerTransport } from "@modelcontextprotocol/sdk/server/stdio.js";
import {
  CallToolRequestSchema,
  ListToolsRequestSchema,
} from "@modelcontextprotocol/sdk/types.js";
import { promises as fs } from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

// Define memory file path using environment variable with fallback
const defaultMemoryPath = path.join(path.dirname(fileURLToPath(import.meta.url)), 'memory.json');

// If MEMORY_FILE_PATH is just a filename, put it in the same directory as the script
const MEMORY_FILE_PATH = process.env.MEMORY_FILE_PATH
  ? path.isAbsolute(process.env.MEMORY_FILE_PATH)
    ? process.env.MEMORY_FILE_PATH
    : path.join(path.dirname(fileURLToPath(import.meta.url)), process.env.MEMORY_FILE_PATH)
  : defaultMemoryPath;

// We are storing our memory using entities, relations, and observations in a graph structure
interface Entity {
  name: string;
  entityType: string;
  observations: string[];
}

interface Relation {
  from: string;
  to: string;
  relationType: string;
}

interface KnowledgeGraph {
  entities: Entity[];
  relations: Relation[];
}

// The KnowledgeGraphManager class contains all operations to interact with the knowledge graph
class KnowledgeGraphManager {
  private async loadGraph(): Promise<KnowledgeGraph> {
    try {
      const data = await fs.readFile(MEMORY_FILE_PATH, "utf-8");
      const lines = data.split("\n").filter(line => line.trim() !== "");
      return lines.reduce((graph: KnowledgeGraph, line) => {
        const item = JSON.parse(line);
        if (item.type === "entity") graph.entities.push(item as Entity);
        if (item.type === "relation") graph.relations.push(item as Relation);
        return graph;
      }, { entities: [], relations: [] });
    } catch (error) {
      return { entities: [], relations: [] };
    }
  }

  private async saveGraph(graph: KnowledgeGraph): Promise<void> {
    const lines = [
      ...graph.entities.map(entity => JSON.stringify({ type: "entity", ...entity })),
      ...graph.relations.map(relation => JSON.stringify({ type: "relation", ...relation }))
    ];
    await fs.writeFile(MEMORY_FILE_PATH, lines.join("\n") + "\n");
  }

  async createEntities(entities: Entity[]): Promise<Entity[]> {
    const graph = await this.loadGraph();
    const newEntities: Entity[] = [];

    for (const entity of entities) {
      const existingEntity = graph.entities.find(e => e.name === entity.name);
      if (!existingEntity) {
        graph.entities.push(entity);
        newEntities.push(entity);
      }
    }

    await this.saveGraph(graph);
    return newEntities;
  }

  async createRelations(relations: Relation[]): Promise<Relation[]> {
    const graph = await this.loadGraph();
    const newRelations: Relation[] = [];

    for (const relation of relations) {
      const existingRelation = graph.relations.find(r => 
        r.from === relation.from && r.to === relation.to && r.relationType === relation.relationType
      );
      if (!existingRelation) {
        graph.relations.push(relation);
        newRelations.push(relation);
      }
    }

    await this.saveGraph(graph);
    return newRelations;
  }

  async addObservations(observations: { entityName: string; observations: string[] }[]): Promise<void> {
    const graph = await this.loadGraph();

    for (const obs of observations) {
      const entity = graph.entities.find(e => e.name === obs.entityName);
      if (entity) {
        entity.observations.push(...obs.observations);
      }
    }

    await this.saveGraph(graph);
  }

  async deleteEntities(entityNames: string[]): Promise<void> {
    const graph = await this.loadGraph();

    graph.entities = graph.entities.filter(e => !entityNames.includes(e.name));
    graph.relations = graph.relations.filter(r => 
      !entityNames.includes(r.from) && !entityNames.includes(r.to)
    );

    await this.saveGraph(graph);
  }

  async deleteObservations(deletions: { entityName: string; observations: string[] }[]): Promise<void> {
    const graph = await this.loadGraph();

    for (const deletion of deletions) {
      const entity = graph.entities.find(e => e.name === deletion.entityName);
      if (entity) {
        entity.observations = entity.observations.filter(obs => 
          !deletion.observations.includes(obs)
        );
      }
    }

    await this.saveGraph(graph);
  }

  async deleteRelations(relations: Relation[]): Promise<void> {
    const graph = await this.loadGraph();

    graph.relations = graph.relations.filter(r => 
      !relations.some(del => 
        r.from === del.from && r.to === del.to && r.relationType === del.relationType
      )
    );

    await this.saveGraph(graph);
  }

  async readGraph(): Promise<KnowledgeGraph> {
    return await this.loadGraph();
  }

  async searchNodes(query: string): Promise<KnowledgeGraph> {
    const graph = await this.loadGraph();
    const lowerQuery = query.toLowerCase();

    const filteredEntities = graph.entities.filter(e => 
      e.name.toLowerCase().includes(lowerQuery) ||
      e.entityType.toLowerCase().includes(lowerQuery) ||
      e.observations.some(obs => obs.toLowerCase().includes(lowerQuery))
    );

    const filteredEntityNames = new Set(filteredEntities.map(e => e.name));
    const filteredRelations = graph.relations.filter(r => 
      filteredEntityNames.has(r.from) && filteredEntityNames.has(r.to)
    );

    return {
      entities: filteredEntities,
      relations: filteredRelations,
    };
  }

  async openNodes(names: string[]): Promise<KnowledgeGraph> {
    const graph = await this.loadGraph();

    const filteredEntities = graph.entities.filter(e => names.includes(e.name));
    const filteredEntityNames = new Set(filteredEntities.map(e => e.name));
    const filteredRelations = graph.relations.filter(r => 
      filteredEntityNames.has(r.from) && filteredEntityNames.has(r.to)
    );

    return {
      entities: filteredEntities,
      relations: filteredRelations,
    };
  }
}

const knowledgeGraphManager = new KnowledgeGraphManager();

