import os
from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from git import Repo
from pathlib import Path

app = FastAPI()

class GitAnalysisRequest(BaseModel):
    repo_path: str
    analysis_type: str

@app.get("/health")
async def health_check():
    return {"status": "healthy"}

@app.post("/analyze")
async def analyze_git(request: GitAnalysisRequest):
    try:
        repo = Repo(request.repo_path)

        if request.analysis_type == "log":
            commits = list(repo.iter_commits(max_count=10))
            return {
                "commits": [
                    {
                        "hash": c.hexsha,
                        "message": c.message,
                        "author": str(c.author),
                        "date": str(c.committed_datetime)
                    }
                    for c in commits
                ]
            }
        elif request.analysis_type == "status":
            return {
                "branch": repo.active_branch.name,
                "modified": [item.a_path for item in repo.index.diff(None)],
                "untracked": repo.untracked_files
            }
        else:
            raise HTTPException(400, "Invalid analysis type")

    except Exception as e:
        raise HTTPException(500, f"Git analysis error: {str(e)}")

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="127.0.0.1", port=8767)

