from fastapi import FastAPI, HTTPException
from pydantic import BaseModel, HttpUrl
import httpx
from selectolax.parser import HTMLParser
import trafilatura

app = FastAPI()

class WebRequest(BaseModel):
    url: HttpUrl
    selector: str = None

@app.get("/health")
async def health_check():
    return {"status": "healthy"}

@app.post("/scrape")
async def scrape_url(request: WebRequest):
    try:
        async with httpx.AsyncClient() as client:
            response = await client.get(str(request.url))
            response.raise_for_status()

            # Use trafilatura for main content extraction
            content = trafilatura.extract(response.text)

            # If selector is provided, use selectolax for specific content
            if request.selector:
                tree = HTMLParser(response.text)
                elements = tree.css(request.selector)
                content = [el.text() for el in elements]

            return {"content": content}

    except httpx.RequestError as e:
        raise HTTPException(500, f"Error fetching URL: {str(e)}")
    except Exception as e:
        raise HTTPException(500, f"Error processing content: {str(e)}")

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="127.0.0.1", port=8768)

