import os
import json
from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
import httpx

app = FastAPI()

class GenerateRequest(BaseModel):
    prompt: str
    model: str = None
    parameters: dict = None

def load_models_config():
    config_path = os.getenv("CUSTOM_MODELS_CONFIG_PATH")
    if not config_path or not os.path.exists(config_path):
        return {}
    with open(config_path) as f:
        return json.load(f)

@app.get("/health")
async def health_check():
    return {"status": "healthy"}

@app.post("/generate")
async def generate(request: GenerateRequest):
    models_config = load_models_config()
    model = request.model or os.getenv("DEFAULT_MODEL", "codellama:13b-instruct")

    if model not in models_config:
        raise HTTPException(400, f"Model {model} not found in configuration")

    config = models_config[model]
    parameters = request.parameters or config.get("parameters", {})

    try:
        async with httpx.AsyncClient() as client:
            response = await client.post(
                f"{os.getenv('CUSTOM_API_URL')}/generate",
                json={
                    "model": model,
                    "prompt": request.prompt,
                    **parameters
                },
                headers={"Authorization": f"Bearer {os.getenv('CUSTOM_API_KEY')}"}
            )
            response.raise_for_status()
            return response.json()

    except httpx.RequestError as e:
        raise HTTPException(500, f"Error calling Ollama API: {str(e)}")
    except Exception as e:
        raise HTTPException(500, f"Error generating response: {str(e)}")

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="127.0.0.1", port=8770)

