from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
import asyncio
import httpx

app = FastAPI()

class BatchRequest(BaseModel):
    operations: list
    timeout: int = 30

@app.get("/health")
async def health_check():
    return {"status": "healthy"}

@app.post("/batch")
async def batch_operations(request: BatchRequest):
    try:
        async with httpx.AsyncClient() as client:
            tasks = []
            for op in request.operations:
                if "url" not in op or "method" not in op:
                    raise HTTPException(400, "Invalid operation format")

                tasks.append(
                    client.request(
                        method=op["method"],
                        url=op["url"],
                        json=op.get("data"),
                        headers=op.get("headers", {}),
                        timeout=request.timeout
                    )
                )

            responses = await asyncio.gather(*tasks, return_exceptions=True)

            results = []
            for i, response in enumerate(responses):
                if isinstance(response, Exception):
                    results.append({
                        "success": False,
                        "error": str(response),
                        "operation_index": i
                    })
                else:
                    results.append({
                        "success": True,
                        "data": response.json(),
                        "operation_index": i
                    })

            return {"results": results}

    except Exception as e:
        raise HTTPException(500, f"Batch operation error: {str(e)}")

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="127.0.0.1", port=8771)

