from typing import Dict, Any, List
from git import Repo
from .base import McpTool

class GitIngestTool(McpTool):
    def __init__(self):
        super().__init__(
            name="git_ingest__analyze",
            description="Analyze git repository"
        )

    def _get_parameters_schema(self) -> Dict[str, Any]:
        return {
            "type": "object",
            "properties": {
                "repo_path": {
                    "type": "string",
                    "description": "Path to the git repository"
                },
                "analysis_type": {
                    "type": "string",
                    "enum": ["log", "status"],
                    "description": "Type of analysis to perform"
                }
            },
            "required": ["repo_path", "analysis_type"]
        }

    async def execute(self, repo_path: str, analysis_type: str) -> Dict[str, Any]:
        try:
            repo = Repo(repo_path)

            if analysis_type == "log":
                commits = list(repo.iter_commits(max_count=10))
                return {
                    "commits": [
                        {
                            "hash": c.hexsha,
                            "message": c.message,
                            "author": str(c.author),
                            "date": str(c.committed_datetime)
                        }
                        for c in commits
                    ]
                }
            elif analysis_type == "status":
                return {
                    "branch": repo.active_branch.name,
                    "modified": [item.a_path for item in repo.index.diff(None)],
                    "untracked": repo.untracked_files
                }
            else:
                return {"error": "Invalid analysis type"}
        except Exception as e:
            return {"error": str(e)}

