from typing import Dict, Any, List
import asyncio
import httpx
from .base import McpTool

class FastMcpTool(McpTool):
    def __init__(self):
        super().__init__(
            name="fastmcp__batch",
            description="Execute batch operations in parallel"
        )

    def _get_parameters_schema(self) -> Dict[str, Any]:
        return {
            "type": "object",
            "properties": {
                "operations": {
                    "type": "array",
                    "items": {
                        "type": "object",
                        "properties": {
                            "url": {
                                "type": "string",
                                "description": "Operation URL"
                            },
                            "method": {
                                "type": "string",
                                "description": "HTTP method"
                            },
                            "data": {
                                "type": "object",
                                "description": "Request data"
                            }
                        },
                        "required": ["url", "method"]
                    }
                },
                "timeout": {
                    "type": "integer",
                    "description": "Operation timeout in seconds"
                }
            },
            "required": ["operations"]
        }

    async def execute(
        self,
        operations: List[Dict[str, Any]],
        timeout: int = 30
    ) -> Dict[str, Any]:
        try:
            async with httpx.AsyncClient() as client:
                tasks = []
                for op in operations:
                    tasks.append(
                        client.request(
                            method=op["method"],
                            url=op["url"],
                            json=op.get("data"),
                            timeout=timeout
                        )
                    )

                responses = await asyncio.gather(*tasks, return_exceptions=True)

                results = []
                for i, response in enumerate(responses):
                    if isinstance(response, Exception):
                        results.append({
                            "success": False,
                            "error": str(response),
                            "operation_index": i
                        })
                    else:
                        results.append({
                            "success": True,
                            "data": response.json(),
                            "operation_index": i
                        })

                return {"results": results}

        except Exception as e:
            return {"error": str(e)}

