{
  "model": "claude-3-5-sonnet-20241022",
  "env": {
    "HIP_VISIBLE_DEVICES": "0",
    "PYTHONPATH": "${workspaceFolder}",
    "POETRY_VIRTUALENVS_IN_PROJECT": "true"
  },
  "permissions": {
    "allow": [
      "Bash(poetry run pytest*)",
      "Bash(poetry run python*)",
      "Bash(poetry run flake8*)",
      "Bash(poetry run black*)",
      "Bash(poetry run mypy*)",
      "Bash(poetry run bandit*)",
      "Bash(python scripts/quality_gates.py)"
    ],
    "deny": [
      "Bash(rm -rf /)",
      "Bash(curl *)",
      "Bash(wget *)"
    ]
  },
  "mcpServers": {
    "sequential-thinking": {
      "command": "npx",
      "args": ["-y", "@modelcontextprotocol/server-sequential-thinking"]
    },
    "memory": {
      "command": "npx",
      "args": ["-y", "@itseasy21/mcp-knowledge-graph"]
    },
    "context7": {
      "command": "npx",
      "args": ["-y", "@upstash/context7-mcp@latest"]
    },
    "filesystem": {
      "command": "python",
      "args": ["-m", "mcp_server_filesystem"],
      "env": {
        "MCP_SERVER_FILESYSTEM_ROOT": "${workspaceFolder}"
      }
    },
    "git-ingest": {
      "command": "python",
      "args": ["-m", "mcp_git_ingest"]
    },
    "web-scraping": {
      "command": "python",
      "args": ["-m", "web_scraping_mcp"]
    },
    "mysql": {
      "command": "python",
      "args": ["-m", "mysql_mcp_server"]
    },
    "zen-mcp": {
      "command": "python",
      "args": ["/home/feanor/Projects/mcp_servers/zen-mcp/server.py"],
      "env": {
        "CUSTOM_API_URL": "http://localhost:11434/v1",
        "CUSTOM_API_KEY": "",
        "CUSTOM_MODEL_NAME": "codellama:13b-instruct",
        "DEFAULT_MODEL": "codellama:13b-instruct",
        "CUSTOM_MODELS_CONFIG_PATH": "/home/feanor/Projects/mcp_servers/zen-mcp/conf/custom_models.json",
        "LOG_LEVEL": "INFO"
      }
    },
    "fastmcp": {
      "command": "python",
      "args": ["-m", "fastmcp"]
    },
    "project-kwe": {
      "command": "poetry",
      "args": ["run", "python", "mcp_server/unified_mcp_server.py"],
      "env": {
        "HIP_VISIBLE_DEVICES": "0",
        "PYTHONPATH": "${workspaceFolder}"
      }
    }
  }
}

