@app.post("/message_queue/send")
async def send_message(message_data: Dict[str, Any] = Body(...)):
    """Send a message to the queue."""
    try:
        if not message_queue:
            raise HTTPException(status_code=400, detail="Message queue not initialized")

        # Validate required fields
        required_fields = ["msg_type", "sender", "recipient", "payload"]
        for field in required_fields:
            if field not in message_data:
                raise HTTPException(status_code=400, detail=f"Missing required field: {field}")

        # Create EnhancedAgentMessage
        message = EnhancedAgentMessage(
            msg_type=MessageType(message_data["msg_type"]),
            sender=message_data["sender"],
            recipient=message_data["recipient"],
            payload=message_data["payload"],
            metadata=message_data.get("metadata", {}),
        )

        # Enqueue the message
        if message_queue.enqueue(message):
            return {
                "success": True,
                "message_id": message.msg_id,
                "timestamp": message.timestamp,
            }
        else:
            raise HTTPException(status_code=503, detail="Message queue is full")

    except ValueError as e:
        raise HTTPException(status_code=400, detail=f"Invalid message type: {e}")
    except Exception as e:
        logger.error(f"Failed to send message: {e}")
        raise HTTPException(status_code=500, detail=str(e))

