#!/usr/bin/env python3
"""
Enhanced Knowledge Catalog with Qdrant Vector Storage
Provides scalable, semantic searchable knowledge storage using Qdrant.
Implements patterns, anti-patterns, and quality scoring with vector embeddings.
"""

import json
import logging
from datetime import datetime
from typing import Dict, List, Optional, Any
from dataclasses import dataclass, asdict
from enum import Enum

from qdrant_client import QdrantClient
from qdrant_client.http import models
from qdrant_client.http.models import Distance, VectorParams, PointStruct

logger = logging.getLogger(__name__)


class PatternType(Enum):
    """Types of patterns that can be stored."""

    SOLUTION = "solution"
    BEST_PRACTICE = "best_practice"
    ARCHITECTURE = "architecture"
    ALGORITHM = "algorithm"
    WORKFLOW = "workflow"


class QualityScore(Enum):
    """Quality scoring for patterns."""

    EXCELLENT = 5
    GOOD = 4
    ACCEPTABLE = 3
    POOR = 2
    UNACCEPTABLE = 1


@dataclass
class Pattern:
    """A successful pattern that can be reused."""

    pattern_id: str
    agent_type: str
    name: str
    description: str
    code_example: str
    context: Dict[str, Any]
    tags: List[str]
    pattern_type: PatternType
    quality_score: QualityScore
    usage_count: int = 0
    created_at: str = ""
    last_used: str = ""


@dataclass
class AntiPattern:
    """An anti-pattern to avoid."""

    anti_pattern_id: str
    agent_type: str
    name: str
    description: str
    why_bad: str
    better_alternative: str
    context: Dict[str, Any]
    tags: List[str]
    created_at: str = ""


class EnhancedKnowledgeCatalog:
    """Enhanced knowledge catalog with Qdrant vector storage."""

    def __init__(
        self,
        storage_path: str = "knowledge_catalog.json",
        qdrant_url: str = "http://localhost:6333",
    ):
        self.storage_path = storage_path
        self.qdrant_url = qdrant_url
        self.collection_name = "enhanced_knowledge_catalog"
        self.vector_size = 1536  # Standard embedding size

        # Fallback storage for compatibility
        self.patterns: Dict[str, Pattern] = {}
        self.anti_patterns: Dict[str, AntiPattern] = {}
        self.agent_knowledge: Dict[str, Dict[str, Any]] = {}
        self.project_knowledge: Dict[str, Dict[str, Dict[str, Any]]] = (
            {}
        )  # project_name -> agent_type -> knowledge

        # Initialize Qdrant
        self.qdrant_client = None
        self.use_qdrant = False
        self._init_qdrant()

        # Load existing data and migrate to Qdrant
        self._load_and_migrate_knowledge()

        # Only seed if no patterns exist in Qdrant
        if not self._has_qdrant_patterns():
            self._seed_current_best_practices()

    def _init_qdrant(self) -> None:
        """Initialize Qdrant client and collection."""
        try:
            self.qdrant_client = QdrantClient(self.qdrant_url)
            self._init_collection()
            self.use_qdrant = True
            logger.info("âœ… Enhanced Knowledge Catalog initialized with Qdrant")
        except Exception as e:
            logger.warning(f"âš ï¸ Qdrant not available, using JSON fallback: {e}")
            self.use_qdrant = False
            self.qdrant_client = None

    def _init_collection(self) -> None:
        """Initialize Qdrant collection for knowledge catalog."""
        try:
            collections = self.qdrant_client.get_collections()
            collection_names = [c.name for c in collections.collections]

            if self.collection_name not in collection_names:
                self.qdrant_client.create_collection(
                    collection_name=self.collection_name,
                    vectors_config=VectorParams(
                        size=self.vector_size, distance=Distance.COSINE
                    ),
                )
                logger.info(f"âœ… Created Qdrant collection: {self.collection_name}")
            else:
                logger.info(
                    f"âœ… Using existing Qdrant collection: {self.collection_name}"
                )

        except Exception as e:
            logger.error(f"âŒ Failed to initialize Qdrant collection: {e}")
            raise

    def _generate_dummy_vector(self) -> List[float]:
        """Generate a dummy vector for testing (in production, use proper embeddings)."""
        return [0.1] * self.vector_size

    def _load_and_migrate_knowledge(self) -> None:
        """Load existing knowledge and migrate to Qdrant if needed."""
        try:
            # Load from JSON for backward compatibility
            with open(self.storage_path, "r") as f:
                data = json.load(f)

                # Convert patterns from serialized format
                self.patterns = {}
                for k, v in data.get("patterns", {}).items():
                    # Convert string back to enum
                    v["pattern_type"] = PatternType(v["pattern_type"])
                    v["quality_score"] = QualityScore(v["quality_score"])
                    self.patterns[k] = Pattern(**v)

                # Convert anti-patterns from serialized format
                self.anti_patterns = {}
                for k, v in data.get("anti_patterns", {}).items():
                    self.anti_patterns[k] = AntiPattern(**v)

                self.agent_knowledge = data.get("agent_knowledge", {})
                self.project_knowledge = data.get("project_knowledge", {})

            # Migrate to Qdrant if available
            if self.use_qdrant:
                self._migrate_to_qdrant()

        except FileNotFoundError:
            logger.info("No existing knowledge catalog found, creating new one")
        except Exception as e:
            logger.error(f"Error loading knowledge catalog: {e}")

    def _migrate_to_qdrant(self) -> None:
        """Migrate existing patterns and anti-patterns to Qdrant."""
        logger.info("ğŸ”„ Migrating existing data to Qdrant...")

        # Migrate patterns
        for pattern_id, pattern in self.patterns.items():
            self._store_pattern_in_qdrant(pattern)

        # Migrate anti-patterns
        for anti_pattern_id, anti_pattern in self.anti_patterns.items():
            self._store_anti_pattern_in_qdrant(anti_pattern)

        logger.info(
            f"âœ… Migrated {len(self.patterns)} patterns and "
            f"{len(self.anti_patterns)} anti-patterns to Qdrant"
        )

    def _store_pattern_in_qdrant(self, pattern: Pattern) -> None:
        """Store a pattern in Qdrant."""
        try:
            # Create vector representation
            vector = self._generate_dummy_vector()

            # Create point for Qdrant
            point = PointStruct(
                id=pattern.pattern_id,
                vector=vector,
                payload={
                    "type": "pattern",
                    "agent_type": pattern.agent_type,
                    "name": pattern.name,
                    "description": pattern.description,
                    "code_example": pattern.code_example,
                    "context": pattern.context,
                    "tags": pattern.tags,
                    "pattern_type": pattern.pattern_type.value,
                    "quality_score": pattern.quality_score.value,
                    "usage_count": pattern.usage_count,
                    "created_at": pattern.created_at,
                    "last_used": pattern.last_used,
                },
            )

            self.qdrant_client.upsert(
                collection_name=self.collection_name, points=[point]
            )
            logger.info(f"âœ… Stored pattern in Qdrant: {pattern.name}")

        except Exception as e:
            logger.error(f"âŒ Failed to store pattern in Qdrant: {e}")

    def _store_anti_pattern_in_qdrant(self, anti_pattern: AntiPattern) -> None:
        """Store an anti-pattern in Qdrant."""
        try:
            # Create vector representation
            vector = self._generate_dummy_vector()

            # Create point for Qdrant
            point = PointStruct(
                id=anti_pattern.anti_pattern_id,
                vector=vector,
                payload={
                    "type": "anti_pattern",
                    "agent_type": anti_pattern.agent_type,
                    "name": anti_pattern.name,
                    "description": anti_pattern.description,
                    "why_bad": anti_pattern.why_bad,
                    "better_alternative": anti_pattern.better_alternative,
                    "context": anti_pattern.context,
                    "tags": anti_pattern.tags,
                    "created_at": anti_pattern.created_at,
                },
            )

            self.qdrant_client.upsert(
                collection_name=self.collection_name, points=[point]
            )
            logger.info(f"âœ… Stored anti-pattern in Qdrant: {anti_pattern.name}")

        except Exception as e:
            logger.error(f"âŒ Failed to store anti-pattern in Qdrant: {e}")

    def _has_qdrant_patterns(self) -> bool:
        """Check if Qdrant has any patterns stored."""
        if not self.use_qdrant:
            return False

        try:
            # Count points in collection
            info = self.qdrant_client.get_collection(self.collection_name)
            return info.points_count > 0
        except Exception as e:
            logger.warning(f"Could not check Qdrant patterns: {e}")
            return False

    def store_pattern(self, pattern: Pattern) -> str:
        """Store a pattern in both Qdrant and JSON fallback."""
        try:
            # Generate ID if not provided
            if not pattern.pattern_id:
                import uuid
                pattern.pattern_id = str(uuid.uuid4())

            # Store in Qdrant if available
            if self.use_qdrant:
                self._store_pattern_in_qdrant(pattern)

            # Store in local memory for backward compatibility
            self.patterns[pattern.pattern_id] = pattern

            # Save to JSON fallback
            self._save_knowledge()

            logger.info(f"âœ… Stored pattern: {pattern.name}")
            return pattern.pattern_id

        except Exception as e:
            logger.error(f"âŒ Failed to store pattern: {e}")
            return ""

    def store_anti_pattern(self, anti_pattern: AntiPattern) -> bool:
        """Store an anti-pattern in both Qdrant and JSON fallback."""
        try:
            # Store in Qdrant if available
            if self.use_qdrant:
                self._store_anti_pattern_in_qdrant(anti_pattern)

            # Store in local memory for backward compatibility
            self.anti_patterns[anti_pattern.anti_pattern_id] = anti_pattern

            # Save to JSON fallback
            self._save_knowledge()

            logger.info(f"âœ… Stored anti-pattern: {anti_pattern.name}")
            return True

        except Exception as e:
            logger.error(f"âŒ Failed to store anti-pattern: {e}")
            return False

    def search_patterns(
        self, query: str, agent_type: Optional[str] = None, limit: int = 10
    ) -> List[Dict[str, Any]]:
        """Search patterns using Qdrant semantic search."""
        try:
            if not self.use_qdrant:
                # Fallback to local search
                return self._search_patterns_local(query, agent_type, limit)

            # Create query vector
            query_vector = self._generate_dummy_vector()

            # Build filter
            filter_conditions = [
                models.FieldCondition(
                    key="type", match=models.MatchValue(value="pattern")
                )
            ]
            if agent_type:
                filter_conditions.append(
                    models.FieldCondition(
                        key="agent_type", match=models.MatchValue(value=agent_type)
                    )
                )

            # Search in Qdrant
            results = self.qdrant_client.search(
                collection_name=self.collection_name,
                query_vector=query_vector,
                query_filter=models.Filter(must=filter_conditions),
                limit=limit,
            )

            # Convert results to dictionary format
            patterns = []
            for result in results:
                payload = result.payload
                patterns.append(
                    {
                        "pattern_id": result.id,
                        "agent_type": payload.get("agent_type"),
                        "name": payload.get("name"),
                        "description": payload.get("description"),
                        "code_example": payload.get("code_example"),
                        "context": payload.get("context", {}),
                        "tags": payload.get("tags", []),
                        "pattern_type": payload.get("pattern_type"),
                        "quality_score": payload.get("quality_score"),
                        "usage_count": payload.get("usage_count", 0),
                        "score": result.score,
                    }
                )

            return patterns

        except Exception as e:
            logger.error(f"âŒ Failed to search patterns: {e}")
            return []

    def _search_patterns_local(
        self, query: str, agent_type: Optional[str] = None, limit: int = 10
    ) -> List[Dict[str, Any]]:
        """Fallback local search when Qdrant is not available."""
        patterns = []
        for pattern in self.patterns.values():
            if agent_type and pattern.agent_type != agent_type:
                continue

            # Simple text search
            if (
                query.lower() in pattern.name.lower()
                or query.lower() in pattern.description.lower()
                or any(query.lower() in tag.lower() for tag in pattern.tags)
            ):
                patterns.append(asdict(pattern))
                if len(patterns) >= limit:
                    break

        return patterns

    def get_statistics(self) -> Dict[str, Any]:
        """Get comprehensive statistics about the knowledge catalog."""
        try:
            total_patterns = len(self.patterns)
            total_anti_patterns = len(self.anti_patterns)

            # Get Qdrant statistics if available
            qdrant_stats = {}
            if self.use_qdrant:
                try:
                    info = self.qdrant_client.get_collection(self.collection_name)
                    qdrant_stats = {
                        "qdrant_points": info.points_count,
                        "qdrant_vectors": info.vectors_count,
                        "storage_mode": "qdrant",
                    }
                except Exception as e:
                    logger.warning(f"Could not get Qdrant stats: {e}")
                    qdrant_stats = {"storage_mode": "fallback"}
            else:
                qdrant_stats = {"storage_mode": "json_only"}

            # Calculate quality statistics
            quality_scores = [
                pattern.quality_score.value for pattern in self.patterns.values()
            ]
            avg_quality = (
                sum(quality_scores) / len(quality_scores) if quality_scores else 0
            )

            # Group by agent type
            patterns_by_agent = {}
            for pattern in self.patterns.values():
                agent_type = pattern.agent_type
                if agent_type not in patterns_by_agent:
                    patterns_by_agent[agent_type] = 0
                patterns_by_agent[agent_type] += 1

            return {
                "total_patterns": total_patterns,
                "total_anti_patterns": total_anti_patterns,
                "patterns_by_agent": patterns_by_agent,
                "average_quality_score": avg_quality,
                "supported_agents": list(self.agent_knowledge.keys()),
                **qdrant_stats,
            }

        except Exception as e:
            logger.error(f"âŒ Failed to get statistics: {e}")
            return {"error": str(e)}

    def get_agent_knowledge(
        self, agent_type: str, project: Optional[str] = None
    ) -> Dict[str, Any]:
        """Get current best practices knowledge for an agent type, optionally scoped to a project."""
        if project and project in self.project_knowledge:
            return self.project_knowledge[project].get(agent_type, {})
        return self.agent_knowledge.get(agent_type, {})

    def get_patterns_for_agent(
        self, agent_type: str, pattern_type: Optional[PatternType] = None
    ) -> List[Pattern]:
        """Get patterns for a specific agent type."""
        patterns = [p for p in self.patterns.values() if p.agent_type == agent_type]

        if pattern_type:
            patterns = [p for p in patterns if p.pattern_type == pattern_type]

        # Sort by quality score and usage count
        patterns.sort(
            key=lambda p: (p.quality_score.value, p.usage_count), reverse=True
        )
        return patterns

    def get_anti_patterns_for_agent(self, agent_type: str) -> List[AntiPattern]:
        """Get anti-patterns for a specific agent type."""
        return [ap for ap in self.anti_patterns.values() if ap.agent_type == agent_type]

    def submit_feedback(self, pattern_id: str, feedback: Dict[str, Any]) -> bool:
        """Submit feedback for a pattern."""
        if pattern_id not in self.patterns:
            return False

        pattern = self.patterns[pattern_id]

        # Update usage count
        pattern.usage_count += 1

        # Update last used timestamp
        pattern.last_used = datetime.now().isoformat()

        # Update quality score based on feedback
        if "user_satisfaction" in feedback:
            satisfaction = feedback["user_satisfaction"]
            if satisfaction >= 4:
                pattern.quality_score = QualityScore.EXCELLENT
            elif satisfaction >= 3:
                pattern.quality_score = QualityScore.GOOD
            elif satisfaction >= 2:
                pattern.quality_score = QualityScore.ACCEPTABLE
            else:
                pattern.quality_score = QualityScore.POOR

        # Save to both Qdrant and JSON
        if self.use_qdrant:
            self._store_pattern_in_qdrant(pattern)

        self._save_knowledge()

        logger.info(f"Updated pattern {pattern_id} with feedback")
        return True

    def _save_knowledge(self) -> None:
        """Save knowledge to JSON fallback storage."""
        try:
            # Convert patterns to serializable format
            patterns_data = {}
            for k, v in self.patterns.items():
                pattern_dict = asdict(v)
                # Convert enum to string for JSON serialization
                pattern_dict["pattern_type"] = v.pattern_type.value
                pattern_dict["quality_score"] = v.quality_score.value
                patterns_data[k] = pattern_dict

            # Convert anti-patterns to serializable format
            anti_patterns_data = {}
            for k, v in self.anti_patterns.items():
                anti_pattern_dict = asdict(v)
                anti_patterns_data[k] = anti_pattern_dict

            data = {
                "patterns": patterns_data,
                "anti_patterns": anti_patterns_data,
                "agent_knowledge": self.agent_knowledge,
                "project_knowledge": self.project_knowledge,
                "last_updated": datetime.now().isoformat(),
                "storage_mode": (
                    "qdrant_with_json_fallback" if self.use_qdrant else "json_only"
                ),
            }

            with open(self.storage_path, "w") as f:
                json.dump(data, f, indent=2)

        except Exception as e:
            logger.error(f"âŒ Failed to save knowledge: {e}")

