#!/usr/bin/env python3
"""
PyQt6 Frontend Analysis Script
Analyzes all PyQt6 files for signals vs API calls usage patterns
"""

import ast
import os
import re
from pathlib import Path
from typing import Dict, List, Tuple, Any, Set
import json

class PyQt6Analyzer:
    def __init__(self, ui_directory: str):
        self.ui_directory = Path(ui_directory)
        self.analysis_results = {}

    def analyze_all_files(self) -> Dict[str, Any]:
        """Analyze all Python files in the UI directory"""
        python_files = list(self.ui_directory.glob("*.py"))

        results = {
            "summary": {
                "total_files": len(python_files),
                "components_using_signals": 0,
                "components_using_api_calls": 0,
                "components_using_both": 0,
                "components_using_neither": 0,
                "worker_components": 0,
                "tab_components": 0,
                "dialog_components": 0,
                "widget_components": 0
            },
            "file_analysis": {},
            "migration_categories": {
                "ready_for_api": [],
                "needs_migration": [],
                "mixed_approach": [],
                "worker_threads": [],
                "pure_ui": []
            },
            "signal_patterns": {},
            "api_patterns": {},
            "backend_dependencies": {},
            "migration_priorities": []
        }

        for file_path in python_files:
            if file_path.name == "__init__.py":
                continue

            print(f"Analyzing {file_path.name}...")
            file_analysis = self.analyze_file(file_path)
            results["file_analysis"][file_path.name] = file_analysis

            # Update summary counts
            if file_analysis["uses_signals"]:
                results["summary"]["components_using_signals"] += 1
            if file_analysis["uses_api_calls"]:
                results["summary"]["components_using_api_calls"] += 1
            if file_analysis["uses_signals"] and file_analysis["uses_api_calls"]:
                results["summary"]["components_using_both"] += 1
            if not file_analysis["uses_signals"] and not file_analysis["uses_api_calls"]:
                results["summary"]["components_using_neither"] += 1

            # Categorize by component type
            if "worker" in file_path.name.lower():
                results["summary"]["worker_components"] += 1
            elif "tab" in file_path.name.lower():
                results["summary"]["tab_components"] += 1
            elif "dialog" in file_path.name.lower():
                results["summary"]["dialog_components"] += 1
            else:
                results["summary"]["widget_components"] += 1

        # Categorize components for migration
        self._categorize_for_migration(results)

        # Analyze patterns
        self._analyze_patterns(results)

        # Generate migration priorities
        self._generate_migration_priorities(results)

        return results

    def analyze_file(self, file_path: Path) -> Dict[str, Any]:
        """Analyze a single Python file"""
        try:
            with open(file_path, 'r', encoding='utf-8') as f:
                content = f.read()
        except Exception as e:
            return {"error": f"Could not read file: {str(e)}"}

        analysis = {
            "file_name": file_path.name,
            "file_size": len(content),
            "line_count": content.count('\n'),
            "uses_signals": False,
            "uses_api_calls": False,
            "uses_workers": False,
            "uses_backend_controller": False,
            "uses_direct_backend": False,
            "signal_definitions": [],
            "signal_connections": [],
            "api_endpoints": [],
            "backend_imports": [],
            "pyqt_imports": [],
            "worker_usage": [],
            "thread_safety_issues": [],
            "component_type": self._determine_component_type(file_path.name),
            "migration_complexity": "unknown"
        }

        # Analyze imports
        analysis.update(self._analyze_imports(content))

        # Analyze signals
        analysis.update(self._analyze_signals(content))

        # Analyze API calls
        analysis.update(self._analyze_api_calls(content))

        # Analyze workers and threading
        analysis.update(self._analyze_workers(content))

        # Analyze backend usage
        analysis.update(self._analyze_backend_usage(content))

        # Determine migration complexity
        analysis["migration_complexity"] = self._assess_migration_complexity(analysis)

        return analysis

    def _determine_component_type(self, filename: str) -> str:
        """Determine the type of component based on filename"""
        filename_lower = filename.lower()

        if "worker" in filename_lower:
            return "worker"
        elif "tab" in filename_lower:
            return "tab"
        elif "dialog" in filename_lower:
            return "dialog"
        elif "widget" in filename_lower:
            return "widget"
        elif "main_window" in filename_lower:
            return "main_window"
        elif filename_lower in ["signals.py", "constants.py", "base_component.py"]:
            return "utility"
        else:
            return "component"

    def _analyze_imports(self, content: str) -> Dict[str, Any]:
        """Analyze import statements"""
        pyqt_imports = []
        backend_imports = []

        # Find PyQt6 imports
        pyqt_patterns = [
            r'from PyQt6\.[^\\s]+ import ([^\\n]+)',
            r'import PyQt6\.[^\\s]+',
        ]

        for pattern in pyqt_patterns:
            matches = re.findall(pattern, content)
            pyqt_imports.extend(matches)

        # Find backend imports
        backend_patterns = [
            r'from src\.backend\.([^\\s]+) import ([^\\n]+)',
            r'import src\.backend\.([^\\s]+)',
        ]

        for pattern in backend_patterns:
            matches = re.findall(pattern, content)
            backend_imports.extend([f"{match[0]}.{match[1]}" if len(match) > 1 else match[0] for match in matches])

        return {
            "pyqt_imports": pyqt_imports,
            "backend_imports": backend_imports,
            "uses_backend_controller": "backend_controller" in content.lower(),
            "uses_direct_backend": len(backend_imports) > 0
        }

    def _analyze_signals(self, content: str) -> Dict[str, Any]:
        """Analyze signal usage"""
        signal_definitions = []
        signal_connections = []

        # Find signal definitions
        signal_def_patterns = [
            r'(\w+)\s*=\s*pyqtSignal\([^)]*\)',
            r'(\w+)\s*=\s*Signal\([^)]*\)',
        ]

        for pattern in signal_def_patterns:
            matches = re.findall(pattern, content)
            signal_definitions.extend(matches)

        # Find signal connections
        connection_patterns = [
            r'(\w+)\.connect\(',
            r'\.(\w+)\.connect\(',
            r'self\.(\w+)\.connect\(',
        ]

        for pattern in connection_patterns:
            matches = re.findall(pattern, content)
            signal_connections.extend(matches)

        return {
            "uses_signals": len(signal_definitions) > 0 or len(signal_connections) > 0,
            "signal_definitions": signal_definitions,
            "signal_connections": signal_connections
        }

    def _analyze_api_calls(self, content: str) -> Dict[str, Any]:
        """Analyze API call usage"""
        api_endpoints = []

        # Find API calls
        api_patterns = [
            r'requests\.(get|post|put|delete)\s*\([^)]*["\']([^"\']+)["\']',
            r'httpx\.(get|post|put|delete)\s*\([^)]*["\']([^"\']+)["\']',
            r'self\.session\.(get|post|put|delete)\s*\([^)]*["\']([^"\']+)["\']',
            r'api_client\.\w+\([^)]*["\']([^"\']+)["\']',
        ]

        for pattern in api_patterns:
            matches = re.findall(pattern, content)
            for match in matches:
                if isinstance(match, tuple) and len(match) >= 2:
                    method = match[0] if len(match) > 1 else "unknown"
                    endpoint = match[1] if len(match) > 1 else match[0]
                else:
                    method = "unknown"
                    endpoint = str(match)
                api_endpoints.append({"method": method, "endpoint": endpoint})

        return {
            "uses_api_calls": len(api_endpoints) > 0 or "requests." in content or "httpx." in content or "api_client" in content,
            "api_endpoints": api_endpoints
        }

    def _analyze_workers(self, content: str) -> Dict[str, Any]:
        """Analyze worker and threading usage"""
        worker_usage = []
        thread_safety_issues = []

        # Find worker patterns
        worker_patterns = [
            r'QThread',
            r'QObject',
            r'moveToThread',
            r'QTimer\.singleShot',
            r'QApplication\.processEvents',
        ]

        for pattern in worker_patterns:
            if re.search(pattern, content):
                worker_usage.append(pattern)

        # Check for thread safety issues
        unsafe_patterns = [
            r'\.setText\(',
            r'\.setEnabled\(',
            r'\.setVisible\(',
            r'\.update\(',
            r'\.repaint\(',
        ]

        # Only flag as unsafe if found outside of main thread context
        for pattern in unsafe_patterns:
            matches = re.finditer(pattern, content)
            for match in matches:
                # Check if it's in a worker context (simple heuristic)
                line_start = content.rfind('\n', 0, match.start())
                line_content = content[line_start:match.end()]
                if 'def run(' in content and 'QThread' in content:
                    thread_safety_issues.append(f"Potential unsafe UI update: {match.group()}")

        return {
            "uses_workers": len(worker_usage) > 0,
            "worker_usage": worker_usage,
            "thread_safety_issues": thread_safety_issues
        }

    def _analyze_backend_usage(self, content: str) -> Dict[str, Any]:
        """Analyze backend service usage patterns"""
        backend_services = []

        service_patterns = [
            r'ScannerService',
            r'ModelManager',
            r'LinterManager',
            r'AccessControlService',
            r'IntegrationsService',
            r'BackendController',
        ]

        for pattern in service_patterns:
            if re.search(pattern, content):
                backend_services.append(pattern)

        return {
            "backend_services": backend_services,
            "uses_direct_backend": len(backend_services) > 0
        }

    def _assess_migration_complexity(self, analysis: Dict[str, Any]) -> str:
        """Assess the complexity of migrating to API-based approach"""
        complexity_score = 0

        # Factors that increase complexity
        if analysis["uses_signals"]:
            complexity_score += len(analysis["signal_definitions"]) * 2
            complexity_score += len(analysis["signal_connections"]) * 1

        if analysis["uses_direct_backend"]:
            complexity_score += len(analysis["backend_imports"]) * 3
            complexity_score += len(analysis["backend_services"]) * 2

        if analysis["uses_workers"]:
            complexity_score += len(analysis["worker_usage"]) * 1

        if len(analysis["thread_safety_issues"]) > 0:
            complexity_score += len(analysis["thread_safety_issues"]) * 2

        # Factors that decrease complexity
        if analysis["uses_api_calls"]:
            complexity_score -= len(analysis["api_endpoints"]) * 1

        if complexity_score <= 2:
            return "low"
        elif complexity_score <= 8:
            return "medium"
        else:
            return "high"

    def _categorize_for_migration(self, results: Dict[str, Any]):
        """Categorize components based on migration needs"""
        for filename, analysis in results["file_analysis"].items():
            if analysis.get("error"):
                continue

            if analysis["component_type"] == "worker":
                results["migration_categories"]["worker_threads"].append(filename)
            elif analysis["uses_api_calls"] and not analysis["uses_direct_backend"]:
                results["migration_categories"]["ready_for_api"].append(filename)
            elif analysis["uses_direct_backend"] and not analysis["uses_api_calls"]:
                results["migration_categories"]["needs_migration"].append(filename)
            elif analysis["uses_api_calls"] and analysis["uses_direct_backend"]:
                results["migration_categories"]["mixed_approach"].append(filename)
            else:
                results["migration_categories"]["pure_ui"].append(filename)

    def _analyze_patterns(self, results: Dict[str, Any]):
        """Analyze common patterns across files"""
        signal_patterns = {}
        api_patterns = {}

        for filename, analysis in results["file_analysis"].items():
            if analysis.get("error"):
                continue

            # Collect signal patterns
            for signal in analysis["signal_definitions"]:
                if signal not in signal_patterns:
                    signal_patterns[signal] = []
                signal_patterns[signal].append(filename)

            # Collect API patterns
            for api_call in analysis["api_endpoints"]:
                endpoint = api_call["endpoint"]
                if endpoint not in api_patterns:
                    api_patterns[endpoint] = []
                api_patterns[endpoint].append(filename)

        results["signal_patterns"] = signal_patterns
        results["api_patterns"] = api_patterns

    def _generate_migration_priorities(self, results: Dict[str, Any]):
        """Generate migration priorities based on complexity and usage"""
        priorities = []

        for filename, analysis in results["file_analysis"].items():
            if analysis.get("error"):
                continue

            priority_score = 0

            # High priority if using direct backend
            if analysis["uses_direct_backend"]:
                priority_score += 10

            # Medium priority if using signals extensively
            if len(analysis["signal_definitions"]) > 3:
                priority_score += 5

            # Low priority if already using APIs
            if analysis["uses_api_calls"]:
                priority_score -= 3

            # Critical priority if thread safety issues
            if len(analysis["thread_safety_issues"]) > 0:
                priority_score += 15

            # Adjust by complexity
            complexity_multiplier = {"low": 0.5, "medium": 1.0, "high": 1.5}
            priority_score *= complexity_multiplier.get(analysis["migration_complexity"], 1.0)

            if priority_score > 0:
                priority_level = "critical" if priority_score >= 15 else "high" if priority_score >= 10 else "medium" if priority_score >= 5 else "low"
                priorities.append({
                    "filename": filename,
                    "priority": priority_level,
                    "score": priority_score,
                    "complexity": analysis["migration_complexity"],
                    "reasons": self._get_migration_reasons(analysis)
                })

        # Sort by priority score
        priorities.sort(key=lambda x: x["score"], reverse=True)
        results["migration_priorities"] = priorities

    def _get_migration_reasons(self, analysis: Dict[str, Any]) -> List[str]:
        """Get reasons why migration is needed"""
        reasons = []

        if analysis["uses_direct_backend"]:
            reasons.append("Uses direct backend imports")
        if len(analysis["signal_definitions"]) > 3:
            reasons.append("Heavy signal usage")
        if len(analysis["thread_safety_issues"]) > 0:
            reasons.append("Thread safety issues")
        if analysis["uses_workers"] and not analysis["uses_api_calls"]:
            reasons.append("Worker threads without API integration")

        return reasons

def generate_migration_map(results: Dict[str, Any]) -> str:
    """Generate a comprehensive migration map"""

    migration_map = f"""
# PyQt6 Frontend Migration Map
## Analysis Date: {Path.cwd()}/pyqt6_analysis_results.json

---

## ðŸ“Š Summary Statistics

- **Total Components**: {results['summary']['total_files']}
- **Using Signals**: {results['summary']['components_using_signals']} ({results['summary']['components_using_signals']/results['summary']['total_files']*100:.1f}%)
- **Using API Calls**: {results['summary']['components_using_api_calls']} ({results['summary']['components_using_api_calls']/results['summary']['total_files']*100:.1f}%)
- **Using Both**: {results['summary']['components_using_both']} ({results['summary']['components_using_both']/results['summary']['total_files']*100:.1f}%)
- **Using Neither**: {results['summary']['components_using_neither']} ({results['summary']['components_using_neither']/results['summary']['total_files']*100:.1f}%)

### Component Types
- **Tab Components**: {results['summary']['tab_components']}
- **Worker Components**: {results['summary']['worker_components']}
- **Dialog Components**: {results['summary']['dialog_components']}
- **Widget Components**: {results['summary']['widget_components']}

---

## ðŸŽ¯ Migration Categories

### âœ… Ready for API ({len(results['migration_categories']['ready_for_api'])} components)
*Components already using API calls with minimal backend dependencies*

"""

    for filename in results['migration_categories']['ready_for_api']:
        analysis = results['file_analysis'][filename]
        migration_map += f"- **{filename}** ({analysis['component_type']}) - {analysis['migration_complexity']} complexity\n"

    migration_map += f"""

### ðŸ”„ Needs Migration ({len(results['migration_categories']['needs_migration'])} components)
*Components using direct backend imports that should be migrated to API calls*

"""

    for filename in results['migration_categories']['needs_migration']:
        analysis = results['file_analysis'][filename]
        backend_services = ', '.join(analysis.get('backend_services', []))
        migration_map += f"- **{filename}** ({analysis['component_type']}) - {analysis['migration_complexity']} complexity\n"
        if backend_services:
            migration_map += f"  - Backend services: {backend_services}\n"

    migration_map += f"""

### âš–ï¸ Mixed Approach ({len(results['migration_categories']['mixed_approach'])} components)
*Components using both API calls and direct backend - need cleanup*

"""

    for filename in results['migration_categories']['mixed_approach']:
        analysis = results['file_analysis'][filename]
        migration_map += f"- **{filename}** ({analysis['component_type']}) - {analysis['migration_complexity']} complexity\n"

    migration_map += f"""

### ðŸ§µ Worker Threads ({len(results['migration_categories']['worker_threads'])} components)
*Background worker components - may need API integration*

"""

    for filename in results['migration_categories']['worker_threads']:
        analysis = results['file_analysis'][filename]
        migration_map += f"- **{filename}** ({analysis['component_type']}) - {analysis['migration_complexity']} complexity\n"

    migration_map += f"""

### ðŸŽ¨ Pure UI ({len(results['migration_categories']['pure_ui'])} components)
*UI-only components with no backend dependencies*

"""

    for filename in results['migration_categories']['pure_ui']:
        analysis = results['file_analysis'][filename]
        migration_map += f"- **{filename}** ({analysis['component_type']}) - {analysis['migration_complexity']} complexity\n"

    migration_map += """

---

## ðŸš€ Migration Priorities

"""

    priority_groups = {"critical": [], "high": [], "medium": [], "low": []}
    for item in results['migration_priorities']:
        priority_groups[item['priority']].append(item)

    for priority_level, items in priority_groups.items():
        if items:
            migration_map += f"""
### {priority_level.upper()} Priority ({len(items)} components)

"""
            for item in items:
                analysis = results['file_analysis'][item['filename']]
