#!/usr/bin/env python3
"""
Backend Controller Integration Test
Tests backend_controller.py integration with each PyQt6 tab
"""

import ast
import importlib.util
import inspect
import os
import sys
import traceback
from pathlib import Path
from typing import Dict, List, Tuple, Any, Optional
import json

# Add project root to Python path
project_root = Path(__file__).parent
sys.path.insert(0, str(project_root))

class BackendControllerTester:
    def __init__(self):
        self.test_results = {}
        self.integration_patterns = {}
        self.broken_communications = []
        self.working_integrations = []

    def test_all_tabs(self) -> Dict[str, Any]:
        """Test backend_controller integration with all tabs"""
        ui_directory = Path("src/frontend/ui")
        tab_files = [f for f in ui_directory.glob("*tab*.py") if f.name != "__init__.py"]

        results = {
            "summary": {
                "total_tabs": len(tab_files),
                "tabs_using_backend_controller": 0,
                "working_integrations": 0,
                "broken_integrations": 0,
                "import_errors": 0,
                "integration_patterns": {}
            },
            "detailed_results": {},
            "backend_controller_analysis": {},
            "integration_issues": [],
            "recommendations": []
        }

        # First, analyze backend controller implementations
        results["backend_controller_analysis"] = self._analyze_backend_controllers()

        # Test each tab
        for tab_file in tab_files:
            print(f"Testing {tab_file.name}...")
            tab_result = self._test_tab_integration(tab_file)
            results["detailed_results"][tab_file.name] = tab_result

            if tab_result["uses_backend_controller"]:
                results["summary"]["tabs_using_backend_controller"] += 1

                if tab_result["integration_status"] == "working":
                    results["summary"]["working_integrations"] += 1
                elif tab_result["integration_status"] == "broken":
                    results["summary"]["broken_integrations"] += 1

                if tab_result.get("import_error"):
                    results["summary"]["import_errors"] += 1

        # Analyze patterns and generate recommendations
        self._analyze_integration_patterns(results)
        self._generate_recommendations(results)

        return results

    def _analyze_backend_controllers(self) -> Dict[str, Any]:
        """Analyze both backend controller implementations"""
        analysis = {
            "backend_service_controller": {},
            "frontend_controller": {},
            "compatibility_issues": []
        }

        # Analyze backend/services/backend_controller.py
        backend_controller_path = Path("src/backend/services/backend_controller.py")
        if backend_controller_path.exists():
            analysis["backend_service_controller"] = self._analyze_controller_file(backend_controller_path)

        # Analyze frontend/controllers/backend_controller.py
        frontend_controller_path = Path("src/frontend/controllers/backend_controller.py")
        if frontend_controller_path.exists():
            analysis["frontend_controller"] = self._analyze_controller_file(frontend_controller_path)

        # Check for compatibility issues
        analysis["compatibility_issues"] = self._check_controller_compatibility(
            analysis["backend_service_controller"], 
            analysis["frontend_controller"]
        )

        return analysis

    def _analyze_controller_file(self, file_path: Path) -> Dict[str, Any]:
        """Analyze a backend controller file"""
        try:
            with open(file_path, 'r', encoding='utf-8') as f:
                content = f.read()

            # Parse the AST
            tree = ast.parse(content)

            analysis = {
                "file_path": str(file_path),
                "file_size": len(content),
                "classes": [],
                "methods": [],
                "imports": [],
                "service_dependencies": [],
                "api_methods": [],
                "initialization_issues": []
            }

            for node in ast.walk(tree):
                if isinstance(node, ast.ClassDef):
                    if "BackendController" in node.name:
                        class_analysis = self._analyze_controller_class(node, content)
                        analysis["classes"].append(class_analysis)

                elif isinstance(node, ast.Import):
                    for alias in node.names:
                        analysis["imports"].append(alias.name)

                elif isinstance(node, ast.ImportFrom):
                    if node.module:
                        for alias in node.names:
                            import_name = f"{node.module}.{alias.name}"
                            analysis["imports"].append(import_name)

                            # Track service dependencies
                            if "services" in node.module:
                                analysis["service_dependencies"].append(import_name)

            return analysis

        except Exception as e:
            return {"error": str(e), "traceback": traceback.format_exc()}

    def _analyze_controller_class(self, class_node: ast.ClassDef, content: str) -> Dict[str, Any]:
        """Analyze a BackendController class"""
        class_analysis = {
            "name": class_node.name,
            "methods": [],
            "init_method": None,
            "api_methods": [],
            "service_initializations": [],
            "potential_issues": []
        }

        for node in class_node.body:
            if isinstance(node, ast.FunctionDef):
                method_info = {
                    "name": node.name,
                    "args": [arg.arg for arg in node.args.args],
                    "is_async": isinstance(node, ast.AsyncFunctionDef),
                    "decorators": [d.id if isinstance(d, ast.Name) else str(d) for d in node.decorator_list]
                }

                class_analysis["methods"].append(method_info)

                if node.name == "__init__":
                    class_analysis["init_method"] = method_info
                    # Analyze initialization for potential issues
                    init_issues = self._analyze_init_method(node, content)
                    class_analysis["service_initializations"] = init_issues

                # Check if it's an API method (public method that might be called by UI)
                if not node.name.startswith("_") and node.name != "__init__":
                    class_analysis["api_methods"].append(method_info)

        return class_analysis

    def _analyze_init_method(self, init_node: ast.FunctionDef, content: str) -> List[Dict[str, Any]]:
        """Analyze __init__ method for service initialization issues"""
        issues = []

        for node in ast.walk(init_node):
            if isinstance(node, ast.Assign):
                for target in node.targets:
                    if isinstance(target, ast.Attribute) and isinstance(target.value, ast.Name):
                        if target.value.id == "self":
                            # This is a service assignment
                            if isinstance(node.value, ast.Call):
                                service_name = target.attr

                                # Check for potential initialization issues
                                if any(keyword in service_name.lower() for keyword in 
                                      ['qdrant', 'continuous_learning', 'security_intelligence']):
                                    issues.append({
                                        "service": service_name,
                                        "issue": "Potentially problematic service initialization",
                                        "reason": "Service may have external dependencies"
                                    })

        return issues

    def _check_controller_compatibility(self, backend_analysis: Dict, frontend_analysis: Dict) -> List[Dict[str, Any]]:
        """Check compatibility between backend controllers"""
        issues = []

        if not backend_analysis or not frontend_analysis:
            issues.append({
                "type": "missing_controller",
                "message": "One or both backend controllers are missing or failed to analyze"
            })
            return issues

        # Check for method compatibility
        backend_methods = set()
        frontend_methods = set()

        for class_info in backend_analysis.get("classes", []):
            for method in class_info.get("api_methods", []):
                backend_methods.add(method["name"])

        for class_info in frontend_analysis.get("classes", []):
            for method in class_info.get("api_methods", []):
                frontend_methods.add(method["name"])

        # Check for method mismatches
        backend_only = backend_methods - frontend_methods
        frontend_only = frontend_methods - backend_methods

        if backend_only:
            issues.append({
                "type": "method_mismatch",
                "message": f"Backend has methods not in frontend: {backend_only}"
            })

        if frontend_only:
            issues.append({
                "type": "method_mismatch", 
                "message": f"Frontend has methods not in backend: {frontend_only}"
            })

        return issues

    def _test_tab_integration(self, tab_file: Path) -> Dict[str, Any]:
        """Test backend controller integration for a specific tab"""
        result = {
            "file_name": tab_file.name,
            "uses_backend_controller": False,
            "import_path": None,
            "import_error": None,
            "integration_status": "not_applicable",
            "backend_controller_usage": [],
            "method_calls": [],
            "integration_pattern": "none",
            "issues": [],
            "recommendations": []
        }

        try:
            # Read and analyze the file
            with open(tab_file, 'r', encoding='utf-8') as f:
                content = f.read()

            # Check for backend controller imports
            import_patterns = [
                r'from\s+src\.backend\.services\.backend_controller\s+import\s+BackendController',
                r'from\s+src\.frontend\.controllers\.backend_controller\s+import\s+BackendController',
                r'from\s+src\.frontend\.controllers\s+import\s+BackendController'
            ]

            for pattern in import_patterns:
                import re
                if re.search(pattern, content):
                    result["uses_backend_controller"] = True
                    result["import_path"] = pattern
                    break

            if result["uses_backend_controller"]:
                # Analyze usage patterns
                result.update(self._analyze_backend_controller_usage(content))

                # Test actual integration
                result["integration_status"] = self._test_integration_status(tab_file, content)

        except Exception as e:
            result["import_error"] = str(e)
            result["integration_status"] = "error"

        return result

    def _analyze_backend_controller_usage(self, content: str) -> Dict[str, Any]:
        """Analyze how backend controller is used in the tab"""
        usage_analysis = {
            "initialization_pattern": "unknown",
            "method_calls": [],
            "usage_locations": [],
            "dependency_injection": False,
            "direct_instantiation": False,
            "potential_issues": []
        }

        # Check initialization patterns
        if "self.backend_controller = BackendController()" in content:
            usage_analysis["initialization_pattern"] = "direct_instantiation"
            usage_analysis["direct_instantiation"] = True
        elif "backend_controller: BackendController" in content:
            usage_analysis["initialization_pattern"] = "dependency_injection"
            usage_analysis["dependency_injection"] = True

        # Find method calls
        import re
        method_call_pattern = r'(?:self\.)?backend_controller\.(\w+)\s*\('
        method_calls = re.findall(method_call_pattern, content)
        usage_analysis["method_calls"] = list(set(method_calls))

        # Check for potential issues
        if usage_analysis["direct_instantiation"]:
            usage_analysis["potential_issues"].append(
                "Direct instantiation may cause initialization issues"
            )

        if not usage_analysis["method_calls"]:
            usage_analysis["potential_issues"].append(
                "Backend controller imported but no methods called"
            )

        return usage_analysis

    def _test_integration_status(self, tab_file: Path, content: str) -> str:
        """Test if the integration actually works"""
        try:
            # Try to import the tab module
            spec = importlib.util.spec_from_file_location(
                tab_file.stem, tab_file
            )

            if spec is None:
                return "import_failed"

            # Don't actually import to avoid side effects, but check syntax
            try:
                ast.parse(content)
                return "syntax_ok"
            except SyntaxError:
                return "syntax_error"

        except Exception as e:
            return "import_error"

    def _analyze_integration_patterns(self, results: Dict[str, Any]):
        """Analyze patterns across all tab integrations"""
        patterns = {
            "direct_instantiation": 0,
            "dependency_injection": 0,
            "mixed_patterns": 0,
            "unused_imports": 0
        }

        for tab_name, tab_result in results["detailed_results"].items():
            if not tab_result["uses_backend_controller"]:
                continue

            if tab_result.get("direct_instantiation"):
                patterns["direct_instantiation"] += 1
            if tab_result.get("dependency_injection"):
                patterns["dependency_injection"] += 1
            if not tab_result.get("method_calls"):
                patterns["unused_imports"] += 1

        results["summary"]["integration_patterns"] = patterns

    def _generate_recommendations(self, results: Dict[str, Any]):
        """Generate recommendations for fixing integration issues"""
        recommendations = []

        # Check for controller compatibility issues
        controller_analysis = results["backend_controller_analysis"]
        if controller_analysis.get("compatibility_issues"):
            recommendations.append({
                "type": "controller_compatibility",
                "priority": "high",
                "issue": "Backend controller compatibility issues found",
                "solution": "Align method signatures between frontend and backend controllers"
            })

        # Check for broken integrations
        broken_count = results["summary"]["broken_integrations"]
        if broken_count > 0:
            recommendations.append({
                "type": "broken_integrations",
                "priority": "high",
                "issue": f"{broken_count} tabs have broken backend controller integration",
                "solution": "Fix import paths and initialization patterns"
            })

        # Check for unused imports
        patterns = results["summary"]["integration_patterns"]
        if patterns.get("unused_imports", 0) > 0:
            recommendations.append({
                "type": "unused_imports",
                "priority": "medium",
                "issue": f"{patterns['unused_imports']} tabs import backend controller but don't use it",
                "solution": "Remove unused imports or implement proper usage"
            })

        # Check for inconsistent patterns
        if patterns.get("direct_instantiation", 0) > 0 and patterns.get("dependency_injection", 0) > 0:
            recommendations.append({
                "type": "inconsistent_patterns",
                "priority": "medium",
                "issue": "Mixed initialization patterns across tabs",
                "solution": "Standardize on dependency injection pattern for better testability"
            })

        results["recommendations"] = recommendations

def generate_integration_report(results: Dict[str, Any]) -> str:
    """Generate comprehensive integration test report"""

    report = f"""
