    def set_current_user(self, user_info: Dict[str, Any]):
        """Set the current user context."""
        self.current_user = user_info
        self.current_session_token = (
            user_info.get("session_token") if user_info else None
        )
        logger.info(f"[PERM] set_current_user called. user_info: {user_info}")
        if user_info:
            username = user_info.get("username", "Unknown")
            role = user_info.get("role", "Unknown")
            permissions = user_info.get("permissions", [])
            logger.info(
                f"[PERM] set_current_user: username={username}, role={role}, permissions={permissions}"
            )
            if not self.startup_complete:
                logger.info(
                    f"BackendController: User '{username}' set as current user. Startup complete."
                )
                self.startup_complete = True

    def user_has_permission(self, permission: str) -> bool:
        """Check if the current user has a specific permission."""
        if not self.current_user:
            logger.info(
                f"[PERM] user_has_permission: No current user. Permission '{permission}' denied."
            )
            return False
        username = self.current_user.get("username", "Unknown")
        role = self.current_user.get("role", "Unknown")
        permissions = self.current_user.get("permissions", [])
        logger.info(
            f"[PERM] user_has_permission: username={username}, role={role}, permissions={permissions}, checking={permission}"
        )
        # Check for wildcard permission
        if "*" in permissions:
            logger.info(
                f"[PERM] user_has_permission: Permission '{permission}' granted by admin role (forced grant)."
            )
            return True
        # Check specific permission
        has_permission = permission in permissions
        if has_permission:
            logger.info(
                f"[PERM] user_has_permission: Permission '{permission}' granted."
            )
        else:
            logger.info(
                f"[PERM] user_has_permission: Permission '{permission}' denied."
            )
        return has_permission

    def clear_current_user(self) -> None:
        """Clear the current user context (logout)."""
        self.current_user = {}
        logger.info("[PERM] clear_current_user called. User context cleared.")

