    def _make_http_request(self, method: str, url: str, headers: dict, data: Optional[Dict[str, Any]] = None) -> requests.Response:
        """Make HTTP request with the specified method."""
        if method.upper() == "GET":
            response = requests.get(url, headers=headers, timeout=30)
        elif method.upper() == "POST":
            response = requests.post(url, headers=headers, json=data or {}, timeout=30)
        elif method.upper() == "PUT":
            response = requests.put(url, headers=headers, json=data or {}, timeout=30)
        elif method.upper() == "DELETE":
            response = requests.delete(url, headers=headers, timeout=30)
        else:
            raise ValueError(f"Unsupported HTTP method: {method}")
        return response

    def make_request(
        self, endpoint: str, method: str = "GET", data: Optional[Dict[str, Any]] = None
    ) -> dict:
        """Make a request to the backend API."""
        try:
            import requests
            import json
            from urllib.parse import urljoin
            import logging
            import os
            import json as jsonlib

            # Load allow_insecure_ssl from config
            config_path = os.path.join(os.path.dirname(__file__), '../../../config/app_config.json')
            allow_insecure_ssl = False
            if os.path.exists(config_path):
                with open(config_path, 'r') as f:
                    try:
                        config = jsonlib.load(f)
                        allow_insecure_ssl = config.get('security', {}).get('allow_insecure_ssl', False)
                    except Exception:
                        pass

            # Base URL for the backend API
            base_url = "https://localhost:8443"
            if base_url.startswith("http://"):
                raise RuntimeError("Insecure HTTP URL detected in backend_controller. Refusing to start. Use HTTPS only.")
            url = urljoin(base_url, endpoint)

            # Prepare headers
            headers = {
                "Content-Type": "application/json",
                "Accept": "application/json"
            }

            # Add authentication if available
            if self.current_session_token:
                headers["Authorization"] = f"Bearer {self.current_session_token}"

            # Make the request (respect allow_insecure_ssl)
            if allow_insecure_ssl:
                logging.warning("INSECURE: SSL verification is disabled for local development. Do not use in production!")
                response = requests.request(method, url, headers=headers, json=data, verify=False)
            else:
                response = self._make_http_request(method, url, headers, data)

            # Handle response
            if response.status_code == 200:
                try:
                    return {
                        "status": "success",
                        "message": "Request successful",
                        "data": response.json() if response.content else {}
                    }
                except json.JSONDecodeError:
                    return {
                        "status": "success",
                        "message": "Request successful",
                        "data": {"content": response.text}
                    }
            else:
                return {
                    "status": "error",
                    "message": f"HTTP {response.status_code}: {response.text}",
                    "data": {}
                }

        except requests.exceptions.ConnectionError:
            return {
                "status": "error",
                "message": "Cannot connect to backend API server",
                "data": {}
            }
        except requests.exceptions.Timeout:
            return {
                "status": "error",
                "message": "Request timed out",
                "data": {}
            }
        except Exception as e:
            logger.error(f"Error making backend request: {e}")
            return {
                "status": "error",
                "message": str(e),
                "data": {}
            }

    def get_service(self, service_name: str):
