    def _load_config(self, config_name: str) -> dict:
        """Load configuration from file. Returns empty dict if file doesn't exist."""
        try:
            import json
            import os
            from pathlib import Path

            config_dir = Path("config")
            config_file = config_dir / f"{config_name}.json"

            if config_file.exists():
                with open(config_file, "r", encoding="utf-8") as f:
                    return json.load(f)
            else:
                logger.warning(f"Config file not found: {config_file}")
                return {}
        except Exception as e:
            logger.error(f"Error loading config '{config_name}': {e}")
            return {}

    def _save_config(self, config_name: str, config_data: dict) -> bool:
        """Save configuration to file. Returns True if successful."""
        try:
            import json
            import os
            from pathlib import Path

            config_dir = Path("config")
            config_dir.mkdir(exist_ok=True)
            config_file = config_dir / f"{config_name}.json"

            with open(config_file, "w", encoding="utf-8") as f:
                json.dump(config_data, f, indent=2, ensure_ascii=False)

            logger.info(f"Config saved successfully: {config_file}")
            return True
        except Exception as e:
            logger.error(f"Error saving config '{config_name}': {e}")
            return False


