def analyze_code_file(self, file_path: str) -> Dict[str, Any]:
    """Analyze a code file against the current standard."""
    try:
        if not self._llm_manager:
            logger.error("LLM Manager not available for code standards analysis")
            return {"error": "LLM Manager not available"}
        # Use the actual backend service instead of the frontend controller stub
        result = self._llm_manager.analyze_code_file(file_path)
        # Convert backend result to frontend format
        if hasattr(result, "__dict__"):
            # Convert CodeAnalysisResult object to dict
            result_dict = {
                "file_path": result.file_path,
                "language": (
                    result.language.value
                    if hasattr(result.language, "value")
                    else str(result.language)
                ),
                "total_violations": result.total_violations,
                "error_count": result.error_count,
                "warning_count": result.warning_count,
                "info_count": result.info_count,
                "auto_fixable_count": result.auto_fixable_count,
                "violations": [],
            }
            # Convert violations to dict format
            for violation in result.violations:
                violation_dict = {
                    "line": violation.line_number,
                    "column": violation.column,
                    "severity": (
                        violation.severity.value
                        if hasattr(violation.severity, "value")
                        else str(violation.severity)
                    ),
                    "message": violation.message,
                    "rule_id": violation.rule_id,
                    "auto_fixable": violation.auto_fixable,
                    "suggested_fix": violation.suggested_fix,
                }
                result_dict["violations"].append(violation_dict)
            logger.info(
                f"Code standards analysis completed for {file_path}: {result.total_violations} violations found"
            )
            return result_dict
        else:
            # If it's already a dict, use it as is
            return result
    except Exception as e:
        logger.error(f"Error analyzing code file {file_path}: {e}")
        return {"error": str(e)}

