def user_has_permission(self, permission_key: str) -> bool:
    """Check if the current user has a specific permission."""
    if not self.current_user:
        if hasattr(self, "_startup_complete") and self._startup_complete:
            logger.debug(
                f"[PERM] No current user logged in. Permission '{permission_key}' denied."
            )
        logger.info(
            f"[PERM] user_has_permission: No current user. Permission '{permission_key}' denied."
        )
        return False
    username = self.current_user.get("username", "unknown")
    role = self.current_user.get("role", "unknown")
    logger.info(
        f"[PERM] user_has_permission: username={username}, role={role}, permissions={list(self.current_permissions)}, checking={permission_key}"
    )
    # Always grant all permissions to Admin role (case-insensitive)
    if role and role.lower() == "admin":
        logger.debug(
            f"[PERM] User '{username}' is admin. Permission '{permission_key}' granted by role (forced grant for Admin role)."
        )
        logger.info(
            f"[PERM] user_has_permission: Permission '{permission_key}' granted by admin role (forced grant)."
        )
        return True
    # Check for wildcard permission
    if "*" in self.current_permissions:
        logger.debug(
            f"[PERM] User '{username}' (role: {role}) has '*'. Permission '{permission_key}' granted."
        )
        logger.info(
            f"[PERM] user_has_permission: Permission '{permission_key}' granted by wildcard."
        )
        return True
    # Check for specific permission
    if permission_key in self.current_permissions:
        logger.debug(
            f"[PERM] User '{username}' (role: {role}) has '{permission_key}'. Permission granted."
        )
        logger.info(
            f"[PERM] user_has_permission: Permission '{permission_key}' granted explicitly."
        )
        return True
    logger.debug(
        f"[PERM] User '{username}' (role: {role}) denied permission '{permission_key}'. Available permissions: {list(self.current_permissions)[:5]}..."
    )
    logger.info(
        f"[PERM] user_has_permission: Permission '{permission_key}' denied. username={username}, role={role}, permissions={list(self.current_permissions)}"
    )
    return False

def user_has_any_permission(self, permissions: List[str]) -> bool:
    """Check if the current user has any of the specified permissions."""
    if not self.current_user:
        return False
    # Check for wildcard permission
    if "*" in self.current_permissions:
        return True
    # Check for any specific permission
    for permission in permissions:
        if permission in self.current_permissions:
            return True
    # Check for role-based permissions
    user_role = self.current_user.get("role", "").lower()
    if user_role == "admin":
        return True
    return False

def user_has_all_permissions(self, permissions: List[str]) -> bool:
    """Check if the current user has all of the specified permissions."""
    if not self.current_user:
        return False
    # Check for wildcard permission
    if "*" in self.current_permissions:
        return True
    # Check for all specific permissions
    for permission in permissions:
        if permission not in self.current_permissions:
            # Check for role-based permissions
            user_role = self.current_user.get("role", "").lower()
            if user_role != "admin":
                return False
    return True

def check_permission(self, session_token: str, permission_key: str) -> bool:
    """Check if the session has the required permission."""
    if not self.current_user or self.current_session_token != session_token:
        return False
    return self.user_has_permission(permission_key)

