   175â†’// AFTER: State machine approach
   176â†’type AsyncState =
   177â†’    | { status: 'idle' }
   178â†’    | { status: 'loading' }
   179â†’    | { status: 'success'; data: T }
   180â†’    | { status: 'error'; error: string };
   181â†’
   182â†’const [state, setState] = useState<AsyncState>({ status: 'idle' });
