#!/usr/bin/env python3
"""
Enhanced Memory Agent with PostgreSQL Temporal Memory Integration

This extends the existing EnhancedMemoryAgent to integrate with the PostgreSQL
temporal memory system, following the 
KWEMemorySystemLlamaIndexIntegrationArchitecture.md
"""

import logging
import time
import uuid
from datetime import datetime
from typing import Dict, Any, List, Optional
from dataclasses import dataclass

# Import existing memory agent
try:
    from enhanced_consolidated_agents import EnhancedMemoryAgent

    ENHANCED_AGENT_AVAILABLE = True
except ImportError:
    ENHANCED_AGENT_AVAILABLE = False

# Import PostgreSQL temporal memory
try:
    from .postgresql_temporal_memory import (
        get_temporal_memory,
        log_agent_action,
        TemporalMemoryEntry,
    )
except ImportError:
    try:
        from postgresql_temporal_memory import (
            get_temporal_memory,
            log_agent_action,
            TemporalMemoryEntry,
        )
    except ImportError:
        # Fallback if module not available
        get_temporal_memory = None
        log_agent_action = None
        TemporalMemoryEntry = None

# Import Redis caching layer
try:
    from .redis_cache_layer import get_redis_cache
except ImportError:
    try:
        from redis_cache_layer import get_redis_cache
    except ImportError:
        get_redis_cache = None

# Import Neo4j relational memory
try:
    from .neo4j_relational_memory import (
        Neo4jRelationalMemory,
        get_neo4j_relational_memory,
        initialize_neo4j_relational_memory,
        KWEGraphNode,
        KWEGraphRelationship,
        GraphQueryResult,
    )
except ImportError:
    try:
        from neo4j_relational_memory import (
            Neo4jRelationalMemory,
            get_neo4j_relational_memory,
            initialize_neo4j_relational_memory,
            KWEGraphNode,
            KWEGraphRelationship,
            GraphQueryResult,
        )
    except ImportError:
        Neo4jRelationalMemory = None
        get_neo4j_relational_memory = None
        initialize_neo4j_relational_memory = None
        KWEGraphNode = None
        KWEGraphRelationship = None
        GraphQueryResult = None

# Import LlamaIndex components for unified interface
try:
    from llama_index.core import VectorStoreIndex, Document, Settings
    from llama_index.vector_stores.qdrant import QdrantVectorStore
    from llama_index.llms.ollama import Ollama
    from llama_index.embeddings.ollama import OllamaEmbedding
    from qdrant_client import QdrantClient

    LLAMAINDEX_AVAILABLE = True
except ImportError:
    LLAMAINDEX_AVAILABLE = False

logger = logging.getLogger(__name__)


@dataclass
class MemoryOperationResult:
    """Result of a memory operation with temporal tracking"""

    success: bool
    operation_id: str
    temporal_log_id: Optional[str] = None
    message: str = ""
    data: Optional[Dict[str, Any]] = None


class EnhancedMemoryAgentWithPostgreSQL:
    """
    Enhanced Memory Agent integrating PostgreSQL temporal memory

    This implements the unified memory architecture described in the
    KWEMemorySystemLlamaIndexIntegrationArchitecture.md with:
    - Qdrant for semantic memory (vectors)
    - Neo4j for relational memory (graph)
    - PostgreSQL for temporal memory (change logs)
    - Redis for caching (performance)
    """

    def __init__(self):
        self.agent_id = "enhanced_memory_agent_postgresql"
        self.logger = logging.getLogger("EnhancedMemoryAgentWithPostgreSQL")

        # Initialize PostgreSQL temporal memory
        self.temporal_memory = get_temporal_memory()

        # Initialize Redis caching layer
        try:
            self.redis_cache = get_redis_cache()
            self.logger.info("âœ… Redis caching layer initialized")
        except Exception as e:
            self.logger.warning(f"Redis cache initialization failed: {e}")
            self.redis_cache = None

        # Initialize Neo4j relational memory
        try:
            self.neo4j_memory = get_neo4j_relational_memory()
            self.logger.info("âœ… Neo4j relational memory initialized")
        except Exception as e:
            self.logger.warning(f"Neo4j memory initialization failed: {e}")
            self.neo4j_memory = None

        # Initialize LlamaIndex components if available
        if LLAMAINDEX_AVAILABLE:
            self._initialize_llamaindex()
        else:
            self.logger.warning("LlamaIndex not available, temporal memory only mode")

        # Initialize existing enhanced agent if available
        if ENHANCED_AGENT_AVAILABLE:
            try:
                self.enhanced_agent = EnhancedMemoryAgent()
                self.logger.info("âœ… Enhanced MemoryAgent integration initialized")
            except Exception as e:
                self.logger.warning(f"Enhanced agent initialization failed: {e}")
                self.enhanced_agent = None
        else:
            self.enhanced_agent = None

        # Memory operation metrics
        self.operation_metrics = {
            "total_operations": 0,
            "successful_operations": 0,
            "temporal_logs_created": 0,
            "last_operation_time": None,
        }

        self.logger.info("ðŸ§  Enhanced Memory Agent with PostgreSQL initialized")

    def _initialize_llamaindex(self):
        """Initialize LlamaIndex components for unified memory interface"""
        try:
            # Initialize Qdrant client for semantic memory
            self.qdrant_client = QdrantClient("localhost", port=6333)

            # Initialize Ollama LLM and embeddings
            self.llm = Ollama(model="codellama:13b-instruct")
            self.embed_model = OllamaEmbedding(model_name="codellama:13b-instruct")

            # Configure LlamaIndex settings
            Settings.llm = self.llm
            Settings.embed_model = self.embed_model

            self.logger.info("âœ… LlamaIndex components initialized")

        except Exception as e:
            self.logger.error(f"LlamaIndex initialization failed: {e}")
            self.qdrant_client = None
            self.llm = None
            self.embed_model = None

    async def query_memory(
        self, query: str, memory_types: Optional[List[str]] = None
    ) -> MemoryOperationResult:
        """
        Query memory across all available memory systems

        Args:
            query: The query string
            memory_types: List of memory types to query
                        (qdrant, neo4j, postgresql, redis)

        Returns:
            MemoryOperationResult with unified results
        """
        operation_id = str(uuid.uuid4())
        start_time = time.time()

        # Default to all memory types if none specified
        if memory_types is None:
            memory_types = ["redis", "qdrant", "neo4j", "postgresql"]

        # Check Redis cache first if available
        if self.redis_cache and "redis" in memory_types:
            cached_result = self.redis_cache.get_cached_memory_query(
                query=query, memory_types=memory_types
            )
            if cached_result:
                self.logger.info(f"ðŸŽ¯ Cache hit for query: {query[:50]}...")
                return MemoryOperationResult(
                    success=True,
                    operation_id=operation_id,
                    message=f"Results retrieved from cache in "
                    f"{time.time() - start_time:.3f}s",
                    data=cached_result,
                )

        results = {}
        total_results = 0

        # Query Qdrant semantic memory
        if "qdrant" in memory_types and LLAMAINDEX_AVAILABLE:
            try:
                qdrant_results = self._query_qdrant_semantic_memory(query)
                if qdrant_results:
                    results["semantic_memory"] = qdrant_results
                    total_results += len(qdrant_results)
            except Exception as e:
                self.logger.error(f"Qdrant query failed: {e}")

        # Query Neo4j relational memory
        if "neo4j" in memory_types and self.neo4j_memory:
            try:
                neo4j_results = self._query_neo4j_relational_memory(query)
                if neo4j_results:
                    results["relational_memory"] = neo4j_results
                    total_results += len(neo4j_results)
            except Exception as e:
                self.logger.error(f"Neo4j query failed: {e}")

        # Query PostgreSQL temporal memory
        if "postgresql" in memory_types and self.temporal_memory:
            try:
                temporal_results = self._query_postgresql_temporal_memory(query)
                if temporal_results:
                    results["temporal_memory"] = temporal_results
                    total_results += len(temporal_results)
            except Exception as e:
                self.logger.error(f"PostgreSQL query failed: {e}")

        # Cache the results if Redis is available
        if self.redis_cache and total_results > 0:
            cache_success = self.redis_cache.cache_memory_query(
                query=query,
                memory_types=memory_types,
                result=results,
                ttl=1800,  # 30 minutes
            )
            if cache_success:
                self.logger.info(f"ðŸ’¾ Cached query results for: {query[:50]}...")

        # Log the operation to temporal memory
        if self.temporal_memory:
            await log_agent_action(
                agent_id=self.agent_id,
                action_type="memory_query",
                change_summary=f"Query: {query[:100]}...",
                details={
                    "query": query,
                    "memory_types": memory_types,
                    "results_count": total_results,
                    "operation_id": operation_id,
                },
            )

        return MemoryOperationResult(
            success=True,
            operation_id=operation_id,
            message=f"Query completed in {time.time() - start_time:.3f}s",
            data=results,
        )

    def _query_neo4j_relational_memory(self, query: str) -> List[Dict]:
        """Query Neo4j relational memory for graph-based relationships"""
        if not self.neo4j_memory:
            return []

        try:
            # Use the Neo4j memory system to query relationships
            graph_results = self.neo4j_memory.query_graph(query)

            # Convert to standardized format
            results = []
            for result in graph_results:
                if isinstance(result, GraphQueryResult):
                    results.append(
                        {
                            "type": "graph_relationship",
                            "source": (
                                result.source_node.properties
                                if result.source_node
                                else None
                            ),
                            "target": (
                                result.target_node.properties
                                if result.target_node
                                else None
                            ),
                            "relationship": (
                                result.relationship.properties
                                if result.relationship
                                else None
                            ),
                            "score": result.score,
                            "path": result.path,
                        }
                    )
                else:
                    results.append(
                        {
                            "type": "graph_node",
                            "node": (
                                result.properties
                                if hasattr(result, "properties")
                                else result
                            ),
                            "score": getattr(result, "score", 1.0),
                        }
                    )

            return results

        except Exception as e:
            self.logger.error(f"Neo4j relational memory query failed: {e}")
            return []

    def _query_qdrant_semantic_memory(self, query: str) -> List[Dict]:
        """Query Qdrant semantic memory for vector-based similarity"""
        if not LLAMAINDEX_AVAILABLE or not self.qdrant_client:
            return []

        try:
            # Create vector store index
            vector_store = QdrantVectorStore(
                client=self.qdrant_client, collection_name="ai_memory"
            )

            # Create index and query
            index = VectorStoreIndex.from_vector_store(vector_store)
            query_engine = index.as_query_engine()
            response = query_engine.query(query)

            return [
                {
                    "type": "semantic_similarity",
                    "content": response.response,
                    "source_nodes": [
                        {
                            "text": node.text,
                            "score": node.score,
                            "metadata": node.metadata,
                        }
                        for node in response.source_nodes
                    ],
                    "score": getattr(response, "score", 1.0),
                }
            ]

        except Exception as e:
            self.logger.error(f"Qdrant semantic memory query failed: {e}")
            return []

    def _query_postgresql_temporal_memory(self, query: str) -> List[Dict]:
        """Query PostgreSQL temporal memory for time-based patterns"""
        if not self.temporal_memory:
            return []

        try:
            # Query temporal memory for recent patterns
            temporal_results = self.temporal_memory.search_temporal_patterns(
                query=query, time_window_hours=24
            )

            return [
                {
                    "type": "temporal_pattern",
                    "pattern": result.pattern,
                    "frequency": result.frequency,
                    "last_occurrence": result.last_occurrence.isoformat(),
                    "confidence": result.confidence,
                }
                for result in temporal_results
            ]

        except Exception as e:
            self.logger.error(f"PostgreSQL temporal memory query failed: {e}")
            return []

    async def store_memory(
        self, content: str, memory_type: str, metadata: Optional[Dict[str, Any]] = None
    ) -> MemoryOperationResult:
        """
        Store memory in the appropriate memory system

        Args:
            content: The content to store
            memory_type: Type of memory (semantic, relational, temporal)
            metadata: Additional metadata for the memory

        Returns:
            MemoryOperationResult with storage status
        """
        operation_id = str(uuid.uuid4())
        start_time = time.time()

        try:
            if memory_type == "semantic" and LLAMAINDEX_AVAILABLE:
                # Store in Qdrant semantic memory
                self._store_semantic_memory(content, metadata)

            elif memory_type == "relational" and self.neo4j_memory:
                # Store in Neo4j relational memory
                self._store_relational_memory(content, metadata)

            elif memory_type == "temporal" and self.temporal_memory:
                # Store in PostgreSQL temporal memory
                await self._store_temporal_memory(content, metadata)

            else:
                return MemoryOperationResult(
                    success=False,
                    operation_id=operation_id,
                    message=f"Memory type '{memory_type}' not supported or "
                    f"system not available",
                )

            # Log the operation
            if self.temporal_memory:
                await log_agent_action(
                    agent_id=self.agent_id,
                    action_type="memory_store",
                    change_summary=f"Stored {memory_type} memory",
                    details={
                        "memory_type": memory_type,
                        "content_length": len(content),
                        "operation_id": operation_id,
                    },
                )

            return MemoryOperationResult(
                success=True,
                operation_id=operation_id,
                message=f"Memory stored in {memory_type} in "
                f"{time.time() - start_time:.3f}s",
            )

        except Exception as e:
            self.logger.error(f"Memory storage failed: {e}")
            return MemoryOperationResult(
                success=False,
                operation_id=operation_id,
                message=f"Memory storage failed: {str(e)}",
            )

    def _store_semantic_memory(self, content: str, metadata: Optional[Dict]):
        """Store content in Qdrant semantic memory"""
        if not LLAMAINDEX_AVAILABLE:
            return

        try:
            # Create document
            document = Document(text=content, metadata=metadata or {})

            # Create vector store and index
            vector_store = QdrantVectorStore(
                client=self.qdrant_client, collection_name="ai_memory"
            )

            # Add to index
            index = VectorStoreIndex.from_documents(
                [document], vector_store=vector_store
            )

            self.logger.info("âœ… Content stored in semantic memory")

        except Exception as e:
            self.logger.error(f"Semantic memory storage failed: {e}")

    def _store_relational_memory(self, content: str, metadata: Optional[Dict]):
        """Store content in Neo4j relational memory"""
        if not self.neo4j_memory:
            return

        try:
            # Create graph node from content
            node_id = self.neo4j_memory.create_node(
                label="memory_content",
                properties={
                    "content": content,
                    "timestamp": datetime.now().isoformat(),
                    **(metadata or {}),
                },
            )

            self.logger.info("âœ… Content stored in relational memory")

        except Exception as e:
            self.logger.error(f"Relational memory storage failed: {e}")

    async def _store_temporal_memory(self, content: str, metadata: Optional[Dict]):
        """Store content in PostgreSQL temporal memory"""
        if not self.temporal_memory:
            return

        try:
            # Store in PostgreSQL
            await self.temporal_memory.log_change(
                agent_id=self.agent_id,
                action_type="memory_store",
                change_summary=f"Stored memory content: {content[:100]}...",
                details={"content": content, "metadata": metadata or {}},
            )

            self.logger.info("âœ… Content stored in temporal memory")

        except Exception as e:
            self.logger.error(f"Temporal memory storage failed: {e}")

    async def get_memory_status(self) -> Dict[str, Any]:
        """Get status of all memory systems"""
        status = {
            "agent_id": self.agent_id,
            "timestamp": datetime.now().isoformat(),
            "memory_systems": {},
        }

        # PostgreSQL temporal memory status
        if self.temporal_memory:
            try:
                temporal_status = await self.temporal_memory.get_stats()
                status["memory_systems"]["postgresql"] = {
                    "status": "available",
                    "details": temporal_status,
                }
            except Exception as e:
                status["memory_systems"]["postgresql"] = {
                    "status": "error",
                    "error": str(e),
                }
        else:
            status["memory_systems"]["postgresql"] = {"status": "not_available"}

        # Redis cache status
        if self.redis_cache:
            try:
                cache_status = self.redis_cache.get_stats()
                status["memory_systems"]["redis"] = {
                    "status": "available",
                    "details": cache_status,
                }
            except Exception as e:
                status["memory_systems"]["redis"] = {"status": "error", "error": str(e)}
        else:
            status["memory_systems"]["redis"] = {"status": "not_available"}

        # Neo4j relational memory status
        if self.neo4j_memory:
            try:
                neo4j_status = self.neo4j_memory.get_stats()
                status["memory_systems"]["neo4j"] = {
                    "status": "available",
                    "details": neo4j_status,
                }
            except Exception as e:
