#!/usr/bin/env python3
"""
Test script to validate MCP server configuration for Claude Code.
"""

import json
import subprocess
import sys
from pathlib import Path

def test_server_availability():
    """Test if all configured MCP servers can be started."""
    config_path = Path.home() / ".config/claude-desktop/claude_desktop_config.json"

    if not config_path.exists():
        print("❌ Claude desktop config not found")
        return False

    try:
        with open(config_path) as f:
            config = json.load(f)
    except Exception as e:
        print(f"❌ Error reading config: {e}")
        return False

    servers = config.get("mcpServers", {})
    print(f"Found {len(servers)} configured MCP servers:")

    results = {}
    for name, server_config in servers.items():
        print(f"\n🔍 Testing {name}...")

        command = server_config.get("command")
        args = server_config.get("args", [])

        if command == "npx":
            # Test npm package availability
            try:
                result = subprocess.run([command] + args + ["--help"], 
                                      capture_output=True, timeout=10)
                if result.returncode == 0:
                    print(f"  ✅ {name} - npm package available")
                    results[name] = True
                else:
                    print(f"  ❌ {name} - npm package not found")
                    results[name] = False
            except Exception as e:
                print(f"  ❌ {name} - error: {e}")
                results[name] = False

        elif command == "python":
            # Test Python module/script availability
            if len(args) > 0 and args[0] == "-m":
                # Module test
                module = args[1]
                try:
                    result = subprocess.run([command, "-c", f"import {module}"], 
                                          capture_output=True, timeout=5)
                    if result.returncode == 0:
                        print(f"  ✅ {name} - Python module available")
                        results[name] = True
                    else:
                        print(f"  ❌ {name} - Python module not found")
                        results[name] = False
                except Exception as e:
                    print(f"  ❌ {name} - error: {e}")
                    results[name] = False
            else:
                # Script file test
                script_path = args[0] if args else ""
                if Path(script_path).exists():
                    print(f"  ✅ {name} - Script file exists")
                    results[name] = True
                else:
                    print(f"  ❌ {name} - Script file not found: {script_path}")
                    results[name] = False

    print(f"\n📊 Summary: {sum(results.values())}/{len(results)} servers available")
    return all(results.values())

if __name__ == "__main__":
    success = test_server_availability()
    sys.exit(0 if success else 1)

