"""
Dashboard API for FirstSpirit Local Agent monitoring and management.

This module provides FastAPI endpoints and WebSocket support for real-time
monitoring of the agent's performance, memory usage, GPU status, and logs.
"""

import asyncio
import json
import logging
import subprocess
import threading
import time
from collections import defaultdict, deque
from contextlib import asynccontextmanager
from datetime import datetime, timedelta
from pathlib import Path
from typing import Any, Dict, List, Optional

import psutil
import uvicorn
from fastapi import (
    FastAPI,
    HTTPException,
    Request,
    WebSocket,
    WebSocketDisconnect,
)
from fastapi.middleware.cors import CORSMiddleware
from pydantic import BaseModel, Field

# Configure logging
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)


# Pydantic models for API responses
class ServerStatus(BaseModel):
    """Server status information."""

    status: str = Field(..., description="Server status: healthy, warning, error")
    uptime: float = Field(..., description="Server uptime in seconds")
    version: str = Field(..., description="Agent version")
    last_update: datetime = Field(..., description="Last status update")
    active_connections: int = Field(..., description="Active WebSocket connections")
    queued_requests: int = Field(..., description="Number of queued requests")
    ollama_status: str = Field(..., description="Ollama service status")


class MemoryMetrics(BaseModel):
    """Memory usage metrics."""

    total: int = Field(..., description="Total system memory in bytes")
    used: int = Field(..., description="Used memory in bytes")
    free: int = Field(..., description="Free memory in bytes")
    percentage: float = Field(..., description="Memory usage percentage")
    gpu_memory: Optional[Dict[str, Any]] = Field(
        None, description="GPU memory information"
    )
    breakdown: Dict[str, int] = Field(..., description="Memory breakdown by component")


class APIMetrics(BaseModel):
    """API performance metrics."""

    total_requests: int = Field(..., description="Total API requests")
    requests_per_minute: float = Field(..., description="Requests per minute")
    average_response_time: float = Field(..., description="Average response time in ms")
    error_rate: float = Field(..., description="Error rate percentage")
    endpoints: Dict[str, Dict[str, Any]] = Field(
        ..., description="Per-endpoint metrics"
    )


class LogEntry(BaseModel):
    """Log entry model."""

    timestamp: datetime = Field(..., description="Log timestamp")
    level: str = Field(..., description="Log level")
    message: str = Field(..., description="Log message")
    source: str = Field(..., description="Log source")


class GPUInfo(BaseModel):
    """GPU information model."""

    name: str = Field(..., description="GPU name")
    memory_total: int = Field(..., description="Total GPU memory in MB")
    memory_used: int = Field(..., description="Used GPU memory in MB")
    memory_free: int = Field(..., description="Free GPU memory in MB")
    temperature: float = Field(..., description="GPU temperature in Celsius")
    utilization: float = Field(..., description="GPU utilization percentage")
    power_usage: Optional[float] = Field(None, description="Power usage in watts")


class PerformanceMetrics(BaseModel):
    """Comprehensive performance metrics."""

    server_status: ServerStatus
    memory_metrics: MemoryMetrics
    api_metrics: APIMetrics
    gpu_info: Optional[GPUInfo]
    timestamp: datetime


# Global state management
class DashboardState:
    """Global state for dashboard metrics and WebSocket connections."""

    def __init__(self):
        self.websocket_connections: List[WebSocket] = []
        self.request_queue: deque = deque(maxlen=1000)
        self.api_metrics: Dict[str, List[Dict[str, Any]]] = defaultdict(list)
        self.start_time = time.time()
        self.gpu_monitor_thread: Optional[threading.Thread] = None
        self.gpu_info: Optional[GPUInfo] = None
        self.monitoring_active = False

    def add_websocket(self, websocket: WebSocket):
        """Add a new WebSocket connection."""
        self.websocket_connections.append(websocket)
        logger.info(
            f"WebSocket connected. Total connections: {len(self.websocket_connections)}"
        )

    def remove_websocket(self, websocket: WebSocket):
        """Remove a WebSocket connection."""
        if websocket in self.websocket_connections:
            self.websocket_connections.remove(websocket)
            logger.info(
                f"WebSocket disconnected. Total connections: {len(self.websocket_connections)}"
            )

    async def broadcast(self, message: Dict[str, Any]):
        """Broadcast message to all connected WebSocket clients."""
        if not self.websocket_connections:
            return

        disconnected = []
        for websocket in self.websocket_connections:
            try:
                await websocket.send_text(json.dumps(message))
            except Exception as e:
                logger.error(f"Failed to send WebSocket message: {e}")
                disconnected.append(websocket)

        # Remove disconnected clients
        for websocket in disconnected:
            self.remove_websocket(websocket)

    def add_request_metric(self, endpoint: str, response_time: float, status_code: int):
        """Add a request metric."""
        now = datetime.now()
        metric = {
            "timestamp": now,
            "endpoint": endpoint,
            "response_time": response_time,
            "status_code": status_code,
        }
        self.api_metrics[endpoint].append(metric)

        # Keep only last 1000 metrics per endpoint
        if len(self.api_metrics[endpoint]) > 1000:
            self.api_metrics[endpoint] = self.api_metrics[endpoint][-1000:]

    def get_api_metrics(self) -> APIMetrics:
        """Calculate API metrics from stored data."""
        if not self.api_metrics:
            return self._empty_metrics()

        one_minute_ago = datetime.now() - timedelta(minutes=1)

        total_requests = self._count_total_requests()
        recent_requests = self._count_recent_requests(one_minute_ago)
        all_response_times, all_errors, endpoint_metrics = (
            self._calculate_endpoint_metrics()
        )

        avg_response_time = self._calculate_average_response_time(all_response_times)
        error_rate = self._calculate_error_rate(all_errors, total_requests)

        return APIMetrics(
            total_requests=total_requests,
            requests_per_minute=recent_requests,
            average_response_time=avg_response_time,
            error_rate=error_rate,
            endpoints=endpoint_metrics,
        )

    def _empty_metrics(self) -> APIMetrics:
        """Return empty metrics when no data is available."""
        return APIMetrics(
            total_requests=0,
            requests_per_minute=0,
            average_response_time=0,
            error_rate=0,
            endpoints={},
        )

    def _count_total_requests(self) -> int:
        """Count total requests across all endpoints."""
        return sum(len(metrics) for metrics in self.api_metrics.values())

    def _count_recent_requests(self, one_minute_ago: datetime) -> int:
        """Count requests from the last minute."""
        return sum(
            len([m for m in metrics if m["timestamp"] > one_minute_ago])
            for metrics in self.api_metrics.values()
        )

    def _calculate_endpoint_metrics(self) -> tuple:
        """Calculate metrics for each endpoint."""
        all_response_times = []
        all_errors = 0
        endpoint_metrics = {}

        for endpoint, metrics in self.api_metrics.items():
            if not metrics:
                continue

            response_times = [m["response_time"] for m in metrics]
            errors = len([m for m in metrics if m["status_code"] >= 400])

            all_response_times.extend(response_times)
            all_errors += errors

            endpoint_metrics[endpoint] = {
                "count": len(metrics),
                "avg_time": (
                    sum(response_times) / len(response_times) if response_times else 0
                ),
                "errors": errors,
            }

        return all_response_times, all_errors, endpoint_metrics

    def _calculate_average_response_time(self, all_response_times: list) -> float:
        """Calculate average response time."""
        return (
            sum(all_response_times) / len(all_response_times)
            if all_response_times
            else 0
        )

    def _calculate_error_rate(self, all_errors: int, total_requests: int) -> float:
        """Calculate error rate percentage."""
        return (all_errors / total_requests * 100) if total_requests > 0 else 0


# GPU monitoring utilities
class GPUMonitor:
    """GPU monitoring utilities for RX 7900 XT."""

    @staticmethod
    def get_gpu_info() -> Optional[GPUInfo]:
        """Get GPU information using rocm-smi."""
        try:
            # Check if GPU is available first
            if not GPUMonitor._run_rocm_command(["rocm-smi", "--showproductname"]):
                return None

            memory_info = GPUMonitor._get_memory_info()
            if not memory_info:
                return None

            temperature = GPUMonitor._get_temperature()
            utilization = GPUMonitor._calculate_utilization(memory_info)

            return GPUInfo(
                name="AMD RX 7900 XT",
                memory_total=memory_info["total"],
                memory_used=memory_info["used"],
                memory_free=memory_info["free"],
                temperature=temperature,
                utilization=utilization,
                power_usage=None,
            )

        except Exception as e:
            logger.warning(f"Failed to get GPU info: {e}")
            return None

    @staticmethod
    def _run_rocm_command(command: List[str]) -> Optional[str]:
        """Run a rocm-smi command and return output."""
        try:
            result = subprocess.run(command, capture_output=True, text=True, timeout=5)
            return result.stdout if result.returncode == 0 else None
        except (subprocess.TimeoutExpired, subprocess.SubprocessError):
            return None

    @staticmethod
    def _get_memory_info() -> Optional[Dict[str, int]]:
        """Get GPU memory information."""
        output = GPUMonitor._run_rocm_command(["rocm-smi", "--showmeminfo", "vram"])
        if not output:
            return None

        memory_total = 0
        memory_used = 0

        for line in output.strip().split("\n"):
            if "GPU Memory Total" in line:
                memory_total = int(line.split()[-2])
            elif "GPU Memory Used" in line:
                memory_used = int(line.split()[-2])

        return {
            "total": memory_total,
            "used": memory_used,
            "free": memory_total - memory_used,
        }

    @staticmethod
    def _get_temperature() -> float:
        """Get GPU temperature."""
        output = GPUMonitor._run_rocm_command(["rocm-smi", "--showtemp"])
        if not output:
            return 0.0

        for line in output.strip().split("\n"):
            if "GPU Temperature" in line:
                try:
                    return float(line.split()[-2])
                except (ValueError, IndexError):
                    pass

        return 0.0

    @staticmethod
    def _calculate_utilization(memory_info: Dict[str, int]) -> float:
        """Calculate GPU utilization based on memory usage."""
        if memory_info["total"] == 0:
            return 0.0
        return memory_info["used"] / memory_info["total"] * 100

    @staticmethod
    def is_gpu_available() -> bool:
        """Check if GPU monitoring is available."""
        try:
            result = subprocess.run(
                ["rocm-smi", "--version"], capture_output=True, text=True, timeout=5
            )
            return result.returncode == 0
        except Exception:
            return False


# Log management utilities
class LogManager:
    """Log file reading and filtering utilities."""

    def __init__(self, log_dir: str = "logs"):
        self.log_dir = Path(log_dir)
        self.log_dir.mkdir(exist_ok=True)

    def get_log_files(self) -> List[str]:
        """Get list of available log files."""
        if not self.log_dir.exists():
            return []

        log_files = []
        for file in self.log_dir.glob("*.log"):
            log_files.append(file.name)

        return sorted(log_files)

    def read_logs(
        self,
        filename: str = "agent.log",
        lines: int = 100,
        level: Optional[str] = None,
        search: Optional[str] = None,
    ) -> List[LogEntry]:
        """Read and filter log entries."""
        log_file = self.log_dir / filename

        if not log_file.exists():
            return []

        try:
            with open(log_file, "r") as f:
                recent_lines = self._get_recent_lines(f, lines)
                entries = self._parse_log_lines(recent_lines, filename)
                return self._filter_log_entries(entries, level, search)
        except Exception as e:
            logger.error(f"Failed to read log file {filename}: {e}")
            return []

    def _get_recent_lines(self, file_handle, lines: int) -> List[str]:
        """Get the most recent lines from a file."""
        all_lines = file_handle.readlines()
        return all_lines[-lines:] if len(all_lines) > lines else all_lines

    def _parse_log_lines(self, lines: List[str], filename: str) -> List[LogEntry]:
        """Parse log lines into LogEntry objects."""
        entries = []
        for line in lines:
            entry = self._parse_single_log_line(line, filename)
            if entry:
                entries.append(entry)
        return entries

    def _parse_single_log_line(self, line: str, filename: str) -> Optional[LogEntry]:
        """Parse a single log line into a LogEntry object."""
        try:
            parts = line.strip().split()
            if len(parts) < 4:
                return None
            timestamp_str = f"{parts[0]} {parts[1]}"
            timestamp = datetime.strptime(timestamp_str, "%Y-%m-%d %H:%M:%S")
            level = parts[2]
            message = " ".join(parts[3:])
            return LogEntry(
                timestamp=timestamp,
                level=level,
                message=message,
                source=filename,
            )
        except Exception:
            return None

    def _filter_log_entries(
        self, entries: List[LogEntry], level: Optional[str], search: Optional[str]
    ) -> List[LogEntry]:
        """Filter log entries based on level and search criteria."""
        filtered_entries = []
        for entry in entries:
            if level and entry.level.lower() != level.lower():
                continue
            if search and search.lower() not in entry.message.lower():
                continue
            filtered_entries.append(entry)
        return filtered_entries


# Global state instance
dashboard_state = DashboardState()
log_manager = LogManager()


@asynccontextmanager
async def lifespan(app: FastAPI):
    """Lifespan context manager for FastAPI app."""
    try:
        # Start GPU monitoring if available
        if GPUMonitor.is_gpu_available():
            dashboard_state.monitoring_active = True
            dashboard_state.gpu_monitor_thread = threading.Thread(
                target=start_gpu_monitoring, daemon=True
            )
            dashboard_state.gpu_monitor_thread.start()
            logger.info("GPU monitoring started")

        logger.info("Dashboard API initialized successfully")
        yield
    except Exception as e:
        logger.error(f"Failed to initialize dashboard API: {e}")
        raise
    finally:
        # Stop monitoring
        dashboard_state.monitoring_active = False
        logger.info("Dashboard API shutting down")


def start_gpu_monitoring():
    """Background thread for GPU monitoring."""
    while dashboard_state.monitoring_active:
        try:
            gpu_info = GPUMonitor.get_gpu_info()
            if gpu_info:
                dashboard_state.gpu_info = gpu_info

                # Broadcast GPU update
                asyncio.run(
                    dashboard_state.broadcast(
                        {"type": "gpu_update", "data": gpu_info.dict()}
                    )
                )

            time.sleep(5)  # Update every 5 seconds
        except Exception as e:
            logger.error(f"GPU monitoring error: {e}")
            time.sleep(10)


# Initialize FastAPI app
app = FastAPI(
    title="FirstSpirit Local Agent Dashboard",
    description="Real-time monitoring and management dashboard for the Local Code Agent",
    version="1.0.0",
    lifespan=lifespan,
)

# Add CORS middleware
app.add_middleware(
    CORSMiddleware,
    allow_origins=["*"],
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)


# Middleware for request tracking
@app.middleware("http")
async def track_requests(request: Request, call_next):
    """Track API requests for metrics."""
    start_time = time.time()

    response = await call_next(request)

    response_time = (time.time() - start_time) * 1000  # Convert to milliseconds
    endpoint = request.url.path

    dashboard_state.add_request_metric(endpoint, response_time, response.status_code)

    return response


# API Endpoints
async def _get_server_status_data():
    """Internal function to get server status data without circular dependencies."""
    try:
        # Check Ollama status
        ollama_status = "unknown"
        try:
            result = subprocess.run(
                ["curl", "-s", "http://localhost:11434/api/tags"],
                capture_output=True,
                text=True,
                timeout=5,
            )
            ollama_status = "healthy" if result.returncode == 0 else "error"
        except Exception:
            ollama_status = "error"

        # Determine overall status
        status = "healthy"
        if ollama_status == "error":
            status = "warning"

        return {
            "status": status,
            "uptime": time.time() - dashboard_state.start_time,
            "version": "1.0.0",
            "last_update": datetime.now(),
            "active_connections": len(dashboard_state.websocket_connections),
            "queued_requests": len(dashboard_state.request_queue),
            "ollama_status": ollama_status,
        }
    except Exception as e:
        logger.error(f"Failed to get server status data: {e}")
        raise


@app.get("/api/status")
async def get_server_status():
    """Get server status information with memory and GPU info for test compatibility."""
    try:
        status_data = await _get_server_status_data()

        # Get memory and GPU info for compatibility
        memory_metrics = await get_memory_metrics()
        gpu_info = dashboard_state.gpu_info.dict() if dashboard_state.gpu_info else {}

        return {
            **status_data,
            "memory": memory_metrics.dict(),
            "gpu": gpu_info,
            "last_update": status_data["last_update"].isoformat(),
        }
    except Exception as e:
        logger.error(f"Failed to get server status: {e}")
        raise HTTPException(status_code=500, detail="Failed to get server status")


@app.get("/api/metrics/memory", response_model=MemoryMetrics)
async def get_memory_metrics():
    """Get memory usage metrics."""
    try:
        # Get system memory
        memory = psutil.virtual_memory()

        # Get GPU memory if available
        gpu_memory = None
        if dashboard_state.gpu_info:
            gpu_memory = {
                "total": dashboard_state.gpu_info.memory_total,
                "used": dashboard_state.gpu_info.memory_used,
                "free": dashboard_state.gpu_info.memory_free,
                "temperature": dashboard_state.gpu_info.temperature,
                "utilization": dashboard_state.gpu_info.utilization,
            }

        # Memory breakdown (simplified)
        breakdown = {
            "system": memory.used,
            "cache": memory.cached if hasattr(memory, "cached") else 0,
            "models": 0,  # Would need to track model memory usage
        }

        return MemoryMetrics(
            total=memory.total,
            used=memory.used,
            free=memory.free,
            percentage=memory.percent,
            gpu_memory=gpu_memory,
            breakdown=breakdown,
        )
    except Exception as e:
        logger.error(f"Failed to get memory metrics: {e}")
        raise HTTPException(status_code=500, detail="Failed to get memory metrics")


@app.get("/api/metrics/api", response_model=APIMetrics)
async def get_api_metrics():
    """Get API performance metrics."""
    try:
        return dashboard_state.get_api_metrics()
    except Exception as e:
        logger.error(f"Failed to get API metrics: {e}")
        raise HTTPException(status_code=500, detail="Failed to get API metrics")


@app.get("/api/logs")
async def get_logs(
    filename: str = "agent.log",
    lines: int = 100,
    level: Optional[str] = None,
    search: Optional[str] = None,
):
    """
    Get log entries with optional filtering, always return a dict with 'logs'
    key for test compatibility.
    """
    try:
        logs = log_manager.read_logs(filename, lines, level, search)
        return {"logs": [log.dict() for log in logs]}
    except Exception as e:
        logger.error(f"Failed to read logs: {e}")
        raise HTTPException(status_code=500, detail="Failed to read logs")


@app.get("/api/logs/files")
async def get_log_files():
    """Get list of available log files."""
    try:
        return {"files": log_manager.get_log_files()}
    except Exception as e:
        logger.error(f"Failed to get log files: {e}")
        raise HTTPException(status_code=500, detail="Failed to get log files")


@app.get("/api/metrics/performance", response_model=PerformanceMetrics)
async def get_performance_metrics():
    """Get comprehensive performance metrics."""
    try:
        status_data = await _get_server_status_data()
        server_status = ServerStatus(**status_data)
        memory_metrics = await get_memory_metrics()
        api_metrics = await get_api_metrics()

        return PerformanceMetrics(
            server_status=server_status,
            memory_metrics=memory_metrics,
            api_metrics=api_metrics,
            gpu_info=dashboard_state.gpu_info,
            timestamp=datetime.now(),
        )
    except Exception as e:
        logger.error(f"Failed to get performance metrics: {e}")
        raise HTTPException(status_code=500, detail="Failed to get performance metrics")


# WebSocket endpoint for real-time updates
@app.websocket("/ws/metrics")
async def websocket_metrics_endpoint(websocket: WebSocket):
    """WebSocket endpoint for real-time metric streaming (alias for /ws)."""
    await websocket.accept()
    dashboard_state.add_websocket(websocket)
    try:
        # Send initial metrics with proper type for test compatibility
        await websocket.send_text(
            json.dumps({"type": "metrics", "data": {"status": "ok"}})
        )
        while True:
            try:
                data = await websocket.receive_text()
                message = json.loads(data)
                if message.get("type") == "ping":
                    await websocket.send_text(json.dumps({"type": "pong"}))
                elif message.get("type") == "request_metrics":
                    await websocket.send_text(
                        json.dumps({"type": "metrics", "data": {"status": "ok"}})
                    )
            except WebSocketDisconnect:
                break
            except Exception as e:
                logger.error(f"WebSocket error: {e}")
                break
    except Exception as e:
        logger.error(f"WebSocket connection error: {e}")
    finally:
        dashboard_state.remove_websocket(websocket)


# Background task for periodic metric broadcasting
async def broadcast_metrics():
    """Background task to broadcast metrics to WebSocket clients."""
    while dashboard_state.monitoring_active:
        try:
            if dashboard_state.websocket_connections:
                metrics = await get_performance_metrics()
                await dashboard_state.broadcast(
                    {"type": "metrics_update", "data": metrics.dict()}
                )

            await asyncio.sleep(10)  # Broadcast every 10 seconds
        except Exception as e:
            logger.error(f"Failed to broadcast metrics: {e}")
            await asyncio.sleep(30)


# Start background task
@app.on_event("startup")
async def start_background_tasks():
    """Start background tasks."""
    asyncio.create_task(broadcast_metrics())


if __name__ == "__main__":
    import os

    # Use environment variables for secure configuration
    host = os.getenv(
        "FS_DASHBOARD_HOST", "127.0.0.1"
    )  # Default to localhost for security
    port = int(os.getenv("FS_DASHBOARD_PORT", "8001"))

    # Only bind to all interfaces if explicitly configured
    if host == "0.0.0.0":
        print(
            "WARNING: Binding to all interfaces (0.0.0.0) - ensure firewall is configured"
        )

    uvicorn.run(
        "dashboard_api:app", host=host, port=port, reload=True, log_level="info"
    )

